INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (1, 'The Broken Bubble', 'broken bubble, the', 'The-Broken-Bubble', 'Philip K.', 'Dick', '1988-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheBrokenBubble.jpg', 1, 'Four people''s lives intertwine and collide in this early novel from one of the SF greats
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (2, 'Clans of the Alphane Moon', 'clans of the alphane moon', 'Clans-Of-The-Alphane-Moon', 'Philip K.', 'Dick', '1964-01-01 00:00:00+00', 'https://philipdick.com/images/covers/ClansOfTheAlphaneMoon.jpg', 1, 'On a planet run by escapees from a mental institution, the doctors who arrive to restore order may be the craziest of all.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (3, 'Confessions of a Crap Artist', 'confessions of a crap artist', 'Confessions-Of-A-Crap-Artist', 'Philip K.', 'Dick', '1975-01-01 00:00:00+00', 'https://philipdick.com/images/covers/ConfessionsOfACrapArtist.jpg', 1, '"A funny, horribly accurate portrait of a life in California in the Fifties."—Rolling Stone
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (4, 'The Cosmic Puppets', 'cosmic puppets, the', 'The-Cosmic-Puppets', 'Philip K.', 'Dick', '1957-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheCosmicPuppets.jpg', 1, 'Following an inexplicable urge, Ted Barton returns to his idyllic Virginia hometown for a vacation, but when he gets there, he is shocked to discover that the town has utterly changed. The stores and houses are all different and he doesn’t recognize anybody. The mystery deepens when he checks the town’s historical records . . . and reads that he died nearly twenty years earlier. As he attempts to uncover the secrets of the town, Barton is drawn deeper into the puzzle, and into a supernatural battle that could decide the fate of the universe.', 'https://1999pkdweb.philipdick.com/THE%20COSMIC%20PUPPETS.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 20:59:51.476+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (5, 'Counter-Clock World', 'counter-clock world', 'Counter-Clock-World', 'Philip K.', 'Dick', '1967-01-01 00:00:00+00', 'https://philipdick.com/images/covers/CounterClockWorld.jpg', 1, '“Dick is the American writer who in recent years has most influenced non-American poets, novelists, and essayists.”—Roberto Bolaño
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (6, 'The Crack in Space', 'crack in space, the', 'The-Crack-In-Space', 'Philip K.', 'Dick', '1966-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheCrackInSpace.jpg', 1, '"Dick’s best books always describe a future that is both entirely recognizable and utterly unimaginable."—The New York Times Book Review
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (7, 'Deus Irae', 'deus irae', 'Deus-Irae', 'Philip K.', 'Dick', '1976-01-01 00:00:00+00', 'https://philipdick.com/images/covers/DeusIrae.jpg', 1, 'An artist searches for God so he can paint his portrait in Philip K. Dick’s collaboration with Roger Zelazny.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (46, 'The VALIS Trilogy', 'valis trilogy, the', 'The-VALIS-Trilogy', 'Philip K.', 'Dick', '1982-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheVALISTrilogy.jpg', 1, '', '', true, '2020-10-19 03:10:31.269921+00', '2020-11-04 00:38:30.645+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (8, 'The Divine Invasion', 'divine invasion, the', 'The-Divine-Invasion', 'Philip K.', 'Dick', '1981-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheDivineInvasion.jpg', 1, 'God is not dead, he has merely been exiled to an extraterrestrial planet. And it is on this planet that God meets Herb Asher and convinces him to help retake Earth from the demonic Belial. Featuring virtual reality, parallel worlds, and interstellar travel, The Divine Invasion blends philosophy and adventure in a way few authors can achieve.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (9, 'Do Androids Dream of Electric Sheep?', 'do androids dream of electric sheep?', 'Do-Androids-Dream-Of-Electric-Sheep', 'Philip K.', 'Dick', '1968-01-01 00:00:00+00', 'https://philipdick.com/images/covers/DoAndroidsDreamOfElectricSheep.jpg', 1, 'A masterpiece ahead of its time, a prescient rendering of a dark future, and the inspiration for the blockbuster film Blade Runner
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (10, 'Dr. Bloodmoney', 'dr. bloodmoney', 'Dr-Bloodmoney', 'Philip K.', 'Dick', '1965-01-01 00:00:00+00', 'https://philipdick.com/images/covers/DrBloodmoney.jpg', 1, '"A masterpiece."—Roberto Bolaño
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (11, 'Dr. Futurity', 'dr. futurity', 'Dr-Futurity', 'Philip K.', 'Dick', '1960-01-01 00:00:00+00', 'https://philipdick.com/images/covers/DrFuturity.jpg', 1, 'In a future where death is embraced, a time-traveling doctor is the only one who can save a wounded resistance leader.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (12, 'Eye in the Sky', 'eye in the sky', 'Eye-In-The-Sky', 'Philip K.', 'Dick', '1957-01-01 00:00:00+00', 'https://philipdick.com/images/covers/EyeInTheSky.jpg', 1, '“I have never seen [its] theme handled with greater technical dexterity or given more psychological meaning.”—Fantasy and Science Fiction
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (13, 'Flow My Tears, the Policeman Said', 'flow my tears, the policeman said', 'Flow-My-Tears-The-Policeman-Said', 'Philip K.', 'Dick', '1974-01-01 00:00:00+00', 'https://philipdick.com/images/covers/FlowMyTearsThePolicemanSaid.jpg', 1, '"Dick skillfully explores the psychological ramifications of this nightmare."—The New York Times Review of Books
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (14, 'Galactic Pot-Healer', 'galactic pot-healer', 'Galactic-Pot-Healer', 'Philip K.', 'Dick', '1969-01-01 00:00:00+00', 'https://philipdick.com/images/covers/GalacticPotHealer.jpg', 1, 'A powerful and enigmatic alien recruits humans and aliens to help it restore a sunken cathedral in this touching and hilarious novel.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (88, 'The Selected Letters of Philip K. Dick 1977-1979', 'selected letters of philip k. dick 1977-1979, the', 'The-Selected-Letters-Of-Philip-K-Dick-1977-1979', 'Philip K.', 'Dick', '1993-04-01 00:00:00+00', 'https://philipdick.com/images/covers/TheSelectedLettersOfPhilipKDick1977-1979.jpg', 3, 'Letters by the influential science fiction writer to relatives, agents, and fellow authors reveal his outlook on life and his fiction.', NULL, true, '2020-10-19 19:27:28.939+00', '2020-10-27 01:31:48.197+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (15, 'The Game-Players of Titan', 'game-players of titan, the', 'The-Game-Players-Of-Titan', 'Philip K.', 'Dick', '1963-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheGamePlayersOfTitan.jpg', 1, 'Years ago, Earth and Titan fought a war and Earth lost. The planet was irradiated and most of the surviving population is sterile. The few survivors play an intricate and unending game called Bluff at the behest of the slug-like aliens who rule the planet. At stake in the game are two very important commodities: land and spouses. Pete Garden just lost his wife and Berkeley, California, but he has a plan to win them back. That is, if he isn’t derailed by aliens, psychic traitors, or his new wife.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (16, 'Gather Yourselves Together', 'gather yourselves together', 'Gather-Yourselves-Together', 'Philip K.', 'Dick', '1994-01-01 00:00:00+00', 'https://philipdick.com/images/covers/GatherYourselvesTogether.jpg', 1, 'Gather Yourselves Together is one of Philip K. Dick’s earliest novels, written when he was just twenty-four years old. It tells the story of three Americans left behind in China by their employer, biding their time as the Communists advance. As they while away the days, both the young and naïve Carl Fitter and the older and worldly Verne Tildon vie for the affections of Barbara Mahler, a woman who may not be so tough-as-nails as she acts. But Carl’s innocence and Verne’s boorishness could end up driving Barbara away from both.', 'https://1999pkdweb.philipdick.com/GATHER%20YOURSELVES%20TOGETHER.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 21:10:25.001+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (17, 'Humpty Dumpty in Oakland', 'humpty dumpty in oakland', 'Humpty-Dumpty-In-Oakland', 'Philip K.', 'Dick', '1986-01-01 00:00:00+00', 'https://philipdick.com/images/covers/HumptyDumptyInOakland.jpg', 1, 'Set in San Francisco in the late 1950s, Humpty Dumpty in Oakland is a tragicomedy of misunderstandings among used car dealers and real-estate salesmen: the small-time, struggling individuals for whom Philip K.Dick always reserved his greatest sympathy.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (18, 'In Milton Lumky Territory', 'in milton lumky territory', 'In-Milton-Lumky-Territory', 'Philip K.', 'Dick', '1985-01-01 00:00:00+00', 'https://philipdick.com/images/covers/InMiltonLumkyTerritory.jpg', 1, 'In The Novels of Philip K. Dick, Kim Stanley Robinson states that "In Milton Lumky Territory . . . is probably the best of Dick''s realist novels aside from Confessions of a Crap Artist," and calls it a "bitter indictment of the effects of capitalism." Dick, on the other hand, in his forward, says "This is actually a very funny book, and a good one, too."
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (19, 'Lies, Inc.', 'lies, inc.', 'Lies-Inc', 'Philip K.', 'Dick', '1984-01-01 00:00:00+00', 'https://philipdick.com/images/covers/LiesInc.jpg', 1, '“Philip K. Dick knew better than anyone how to recognize the disturbances of exile.”—Roberto Bolaño
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (20, 'The Man in the High Castle', 'man in the high castle, the', 'The-Man-In-The-High-Castle', 'Philip K.', 'Dick', '1962-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheManInTheHighCastle.jpg', 1, 'The basis for the Amazon Original series
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (21, 'The Man Who Japed', 'man who japed, the', 'The-Man-Who-Japed', 'Philip K.', 'Dick', '1956-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheManWhoJaped.jpg', 1, 'Following a devastating nuclear war, the Moral Reclamation government took over the world and forced its citizens to live by strictly puritanical rules—no premarital sex, drunkenness, or displaying of neon signs—all of which are reinforced through a constant barrage of messaging to the  The chief purveyor of these messages is Alan Purcell, next in line to become head of the propaganda bureau. But there is just one problem: a statue of the government’s founder has been vandalized and the head is hidden in Purcell’s closet. In this buttoned-up society, maybe all a revolution needs is one really great prank . . .', 'https://1999pkdweb.philipdick.com/THE%20MAN%20WHO%20JAPED.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 21:13:17.46+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (89, 'The Selected Letters of Philip K. Dick 1938-1971', 'selected letters of philip k. dick 1938-1971, the', 'The-Selected-Letters-Of-Philip-K-Dick-1938-1971', 'Philip K.', 'Dick', '1997-03-10 00:00:00+00', 'https://philipdick.com/images/covers/TheSelectedLettersOfPhilipKDick1938-1971.jpg', 3, 'Letters by the influential science fiction writer to relatives, agents, and fellow authors reveal his outlook on life and his fiction.', NULL, true, '2020-10-19 20:09:45.968+00', '2020-10-27 01:31:20.215+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (22, 'The Man Whose Teeth Were All Exactly Alike', 'man whose teeth were all exactly alike, the', 'The-Man-Whose-Teeth-Were-All-Exactly-Alike', 'Philip K.', 'Dick', '1984-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheManWhoseTeethWereAllExactlyAlike.jpg', 1, 'Small town America tears itself apart in this realist novel from one of the greats of science fiction
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (23, 'Martian Time-Slip', 'martian time-slip', 'Martian-Time-Slip', 'Philip K.', 'Dick', '1964-01-01 00:00:00+00', 'https://philipdick.com/images/covers/MartianTimeSlip.jpg', 1, '"The writing is humorous, painful, awesome in its effect on both mind and heart . . . There are few modern novels to match it."—Rolling Stone
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (24, 'Mary and the Giant', 'mary and the giant', 'Mary-And-The-Giant', 'Philip K.', 'Dick', '1987-01-01 00:00:00+00', 'https://philipdick.com/images/covers/MaryAndTheGiant.jpg', 1, 'Mary Anne Reynolds is a young and vulnerable woman, determined to make her own way in the world. But Pacific Park, California, in the 1950s is not really the place for Mary. Her relationship with a black singer offends against the small town''s views on sexual mores and exposes its bigoted views on race. This is a powerful portrayal of the claustrophobia of small-town California, and Mary Anne Reynolds is one of the most memorable characters Dick ever created.', 'https://1999pkdweb.philipdick.com/maryand.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 21:15:30.189+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (25, 'A Maze of Death', 'maze of death, a', 'A-Maze-Of-Death', 'Philip K.', 'Dick', '1970-01-01 00:00:00+00', 'https://philipdick.com/images/covers/AMazeOfDeath.jpg', 1, 'A sci-fi murder mystery set on a mysterious planet, with a twist ending that leaves the reader wondering just what they’ve been witnessing the whole time.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (26, 'Nick and the Glimmung', 'nick and the glimmung', 'Nick-And-The-Glimmung', 'Philip K.', 'Dick', '1988-01-01 00:00:00+00', 'https://philipdick.com/images/covers/NickAndTheGlimmung.jpg', 1, 'Nick and his family are forced to leave Earth in order for him to keep his cat, Horace - because all pets are now banned, as they use up badly needed resources. They settle on Plowman''s Planet, where they discover a variety of strange and wonderful alien lifeforms.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (27, 'Now Wait for Last Year', 'now wait for last year', 'Now-Wait-For-Last-Year', 'Philip K.', 'Dick', '1966-01-01 00:00:00+00', 'https://philipdick.com/images/covers/NowWaitForLastYear.jpg', 1, 'Earth is trapped in the crossfire of an unwinnable war between two alien civilizations. Its leader is perpetually on the verge of death. And on top of it all, a new drug has just entered circulation—a drug that haphazardly sends its users traveling through time.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (28, 'Our Friends from Frolix 8', 'our friends from frolix 8', 'Our-Friends-From-Frolix-8', 'Philip K.', 'Dick', '1970-01-01 00:00:00+00', 'https://philipdick.com/images/covers/OurFriendsFromFrolix8.jpg', 1, 'This satirical adventure from Philip K. Dick deals with issues of power, class, and politics, set in a world ruled by big-brained elites.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (29, 'The Penultimate Truth', 'penultimate truth, the', 'The-Penultimate-Truth', 'Philip K.', 'Dick', '1964-01-01 00:00:00+00', 'https://philipdick.com/images/covers/ThePenultimateTruth.jpg', 1, '“At a time when most 20th-century science fiction writers seem hopelessly dated, Dick gives us a vision of the future that captures the feel of our time.”—Wired
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (30, 'Puttering About in a Small Land', 'puttering about in a small land', 'Puttering-About-In-A-Small-Land', 'Philip K.', 'Dick', '1985-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PutteringAboutInASmallLand.jpg', 1, 'When Roger and Virginia Lindhal enroll their son Gregg in Mrs. Alt''s Los Padres Valley School in the mountains of Southern California, their marriage is already in deep trouble. Then the Lindhals meet Chic and Liz Bonner, whose two sons also board at Mrs. Alt''s school. The meeting is a catalyst for a complicated series of emotions and traumas, set against the backdrop of suburban Los Angeles in the early fifties. The buildup of emotional intensity and the finely observed characterizations are hallmarks of Philip K. Dick''s work.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (31, 'Radio Free Albemuth', 'radio free albemuth', 'Radio-Free-Albemuth', 'Philip K.', 'Dick', '1985-01-01 00:00:00+00', 'https://philipdick.com/images/covers/RadioFreeAlbemuth.jpg', 1, 'A visionary alternate history of the United States filled with enough conspiracy theories to thrill the most hardened paranoid, Radio Free Albemuth is proof of Dick’s stature as our century’s greatest science fiction writer.', 'https://1999pkdweb.philipdick.com/RADIO%20FREE%20ALBEMUTH.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 21:16:07.142+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (32, 'A Scanner Darkly', 'scanner darkly, a', 'A-Scanner-Darkly', 'Philip K.', 'Dick', '1977-01-01 00:00:00+00', 'https://philipdick.com/images/covers/AScannerDarkly.jpg', 1, '“Dick is Thoreau plus the death of the American dream.”—Roberto Bolaño
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (33, 'The Simulacra', 'simulacra, the', 'The-Simulacra', 'Philip K.', 'Dick', '1964-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheSimulacra.jpg', 1, 'On a ravaged Earth, fate and circumstances bring together a disparate group of characters, including a fascist with dreams of a coup, a composer who plays his instrument with his mind, a First Lady who calls all the shots, and the world’s last practicing therapist. And they all must contend with an underclass that is beginning to ask a few too many questions, aided by a man called Loony Luke and his very persuasive pet alien.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (34, 'Solar Lottery', 'solar lottery', 'Solar-Lottery', 'Philip K.', 'Dick', '1955-01-01 00:00:00+00', 'https://philipdick.com/images/covers/SolarLottery.jpg', 1, 'In 2203 anyone can become the ruler of the solar system. There are no elections, no interviews, no prerequisites whatsoever—it all comes down to the random turns of a giant wheel. But when a new Quizmaster takes over, the old one still keeps some rights, namely the right to hire an unending stream of assassins to attempt to kill the new leader.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (35, 'The Three Stigmata of Palmer Eldritch', 'three stigmata of palmer eldritch, the', 'The-Three-Stigmata-Of-Palmer-Eldritch', 'Philip K.', 'Dick', '1965-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheThreeStigmataOfPalmerEldritch.jpg', 1, '“A psychedelic odyssey of hallucinations-within-hallucinations from which no reader emerges unscathed.”—Boston Globe
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (36, 'Time Out of Joint', 'time out of joint', 'Time-Out-Of-Joint', 'Philip K.', 'Dick', '1959-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TimeOutOfJoint.jpg', 1, '"Marvelous, terrifying fun, especially if you’ve ever suspected that the world is an unreal construct built solely to keep you from knowing who you really are. Which it is, of course."—Rolling Stone
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (37, 'The Transmigration of Timothy Archer', 'transmigration of timothy archer, the', 'The-Transmigration-Of-Timothy-Archer', 'Philip K.', 'Dick', '1982-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheTransmigrationOfTimothyArcher.jpg', 1, 'The final book in Philip K. Dick’s VALIS trilogy, The Transmigration of Timothy Archer brings the author’s search for the identity and nature of God to a close. The novel follows Bishop Timothy Archer as he travels to Israel, ostensibly to examine ancient scrolls bearing the words of Christ. But, more importantly, this leads him to examine the decisions he made during his life and how they may have contributed to the suicide of his mistress and son.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (38, 'Ubik', 'ubik', 'Ubik', 'Philip K.', 'Dick', '1969-01-01 00:00:00+00', 'https://philipdick.com/images/covers/Ubik.jpg', 1, '“From the stuff of space opera, Dick spins a deeply unsettling existential horror story, a nightmare you’ll never be sure you’ve woken up from.”—Lev Grossman, Time
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (39, 'Ubik: The Screenplay', 'ubik: the screenplay', 'Ubik-The-Screenplay', 'Philip K.', 'Dick', '1985-01-01 00:00:00+00', 'https://philipdick.com/images/covers/UbikTheScreenplay.jpg', 1, '“An accident has occurred. Joe Chip and his colleagues—all but one of them—have narrowly escaped an explosion at a moon base. Or is it the other way round? Did Joe and the others die, and did the one fatality, Glen Runciter, actually survive? . . . From the stuff of space opera, Dick spins a deeply unsettling existential horror story, a nightmare you’ll never be sure you’ve woken up from.”—Lev Grossman, Time
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (40, 'VALIS', 'valis', 'VALIS', 'Philip K.', 'Dick', '1981-01-01 00:00:00+00', 'https://philipdick.com/images/covers/VALIS.jpg', 1, '“Dick is one of the ten best American writers of the twentieth century, which is saying a lot. Dick was a kind of Kafka steeped in LSD and rage.”—Roberto Bolaño
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (41, 'Voices From the Street', 'voices from the street', 'Voices-From-The-Street', 'Philip K.', 'Dick', '2007-01-01 00:00:00+00', 'https://philipdick.com/images/covers/VoicesFromTheStreet.jpg', 1, 'One of Dick''s earliest books but his last to be published, this is the story of one man''s descent into depression and madness - and his escape to the other side
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (42, 'Vulcan''s Hammer', 'vulcan''s hammer', 'Vulcans-Hammer', 'Philip K.', 'Dick', '1960-01-01 00:00:00+00', 'https://philipdick.com/images/covers/VulcansHammer.jpg', 1, 'After the twentieth century’s devastating series of wars, the world’s governments banded together into one globe-spanning entity, committed to peace at all costs. Ensuring that peace is the Vulcan supercomputer, responsible for all major decisions. But some people don’t like being taken out of the equation. And others resent the idea that the Vulcan is taking the place of God. As the world grows ever closer to all-out war, one functionary frantically tries to prevent it. But the Vulcan computer has its own plans, plans that might not include humanity at all.', 'https://1999pkdweb.philipdick.com/VULCANS%20HAMMER.htm', true, '2020-10-19 03:10:31.269921+00', '2020-11-04 00:27:35.012+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (43, 'We Can Build You', 'we can build you', 'We-Can-Build-You', 'Philip K.', 'Dick', '1972-01-01 00:00:00+00', 'https://philipdick.com/images/covers/WeCanBuildYou.jpg', 1, 'In this lyrical and moving novel, Philip K. Dick tells a story of toxic love and compassionate robots. When Louis Rosen''s electronic organ company builds a pitch-perfect robotic replica of Abraham Lincoln, they are pulled into the orbit of a shady businessman, who is looking to use Lincoln for his own profit. Meanwhile, Rosen seeks Lincoln''s advice as he woos a woman incapable of understanding human emotions - someone who may be even more robotic than Lincoln''s replica.', 'https://1999pkdweb.philipdick.com/WE%20CAN%20BUILD%20YOU.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 21:32:40.866+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (44, 'The World Jones Made', 'world jones made, the', 'The-World-Jones-Made', 'Philip K.', 'Dick', '1956-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheWorldJonesMade.jpg', 1, 'Precognition; a world ruled by Relativism; giant alien jellyfish. The World Jones Made is a classic Philip K. Dick mash-up, taking deep philosophical musings and infusing them with wild action.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (45, 'The Zap Gun', 'zap gun, the', 'The-Zap-Gun', 'Philip K.', 'Dick', '1967-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheZapGun.jpg', 1, 'In this biting satire, the Cold War may have ended, but the eastern and western governments never told their citizens. Instead they created an elaborate ruse, wherein each side comes up with increasingly outlandish doomsday weapons—weapons that don’t work. But when aliens invade, the top designers of both sides have to come together to make a real doomsday device—if they don’t kill each other first.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (47, 'Total Recall', 'total recall', 'Total-Recall', 'Philip K.', 'Dick', NULL, 'https://philipdick.com/images/covers/TotalRecall.jpg', 12, '"Second Variety" is an influential short story by Philip K. Dick first published in Space Science Fiction magazine, in May 1953. It is one of Dick''s many stories in which nuclear war has rendered the Earth''s surface to an uninhabitable, gray ash pile, and the only things remaining are killer robots. Philip Kindred Dick was an American novelist, short story writer, and essayist whose published work during his lifetime was almost entirely in the science fiction genre. This early time period from Dick''s life was a difficult and impoverished time for him. Dick wrote "Second Variety" in the early years of his career for a popular science fiction magazine. This story is selected here as one of the best of over 120 short science fiction novels that he wrote for the magazines. It is a pleasure to publish this new, high quality, and affordable edition.', 'https://1999pkdweb.philipdick.com/We%20Can%20Remember%20It%20For%20You%20Wholesale.htm', true, '2020-10-19 03:10:31.269921+00', '2020-10-27 20:52:25.447+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (48, 'Minority Report and Other Stories', 'minority report and other stories', 'Minority-Report-And-Other-Stories', 'Philip K.', 'Dick', '2004-01-07 00:00:00+00', 'https://philipdick.com/images/covers/MinorityReportAndOtherStories.jpg', 2, 'Viewed by many as the greatest science fiction writer on any planet, Philip K. Dick has written some of the most intriguing, original, and thought-provoking fiction of our time. This collection includes stories that will make you laugh, cringe...and stop and think.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (49, 'The Unreconstructed M and Other Stories', 'unreconstructed m and other stories, the', 'The-Unreconstructed-M-And-Other-Stories', 'Philip K.', 'Dick', NULL, 'https://philipdick.com/images/covers/TheUnreconstructedMAndOtherStories.jpg', 2, 'What if...a machine could murder one person while implicating another for the crime? An alien being looked like livestock but spoke like a philosopher? Earth was ruined by war, and Mars was humanity''s only hope? Robots were created to wage an unending war on the ravaged surface of Earth while man hid underground? Peeking into the future showed a worse result with each look?
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (50, 'Volume I: The King of the Elves (The Collected Stories of Philip K. Dick)', 'volume i: the king of the elves (the collected stories of philip k. dick)', 'Volume-I-The-King-Of-The-Elves-The-Collected-Stories-Of-Philip-K-Dick-', 'Philip K.', 'Dick', NULL, 'https://philipdick.com/images/covers/TheKingOfTheElves.jpg', 2, 'Philip K. Dick (1928-1982) was one of the seminal figures of 20th century science fiction. His many stories and novels, which include such classics as The Man in the High Castle and Do Androids Dream of Electric Sheep?, reflect a deeply personal world view, exploring the fragile, multifarious nature of reality itself and examining those elements that make us -- or fail to make us -- fully human. He did as much as anyone to demolish the artificial barrier between genre fiction and "literature," and the best of his work has earned a permanent place in American popular culture.
