INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (65, 'The Collected Short Stories of Philip K. Dick, Volume 4', 'collected short stories of philip k. dick, volume 4, the', 'The-Collected-Short-Stories-Of-Philip-K-Dick-Volume-4', 'Philip K.', 'Dick', NULL, 'https://philipdick.com/images/covers/TheCollectedShortStoriesOfPhilipKDickVolume4.jpg', 2, 'Many thousands of readers worldwide consider Philip K. Dick to have been the greatest science fiction writer on any planet.  Since his untimely death in 1982, interest in Dick’s work has continued to mount and his reputation has been enhanced by a growing body of critical attention. The Philip K. Dick Award is now presented annually to a distinguished work of science fiction, and the Philip K. Dick Society is devoted to the study and promulgation of his works.
