INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (106, 'VALIS: An Opera', 'valis: an opera', 'VALIS-An-Opera', 'Tod', 'Machover', '1987-01-01 00:00:00+00', 'https://philipdick.com/images/covers/VALISAnOpera.jpg', 10, NULL, NULL, true, '2020-10-23 15:11:58.686+00', '2020-11-04 00:38:07.172+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (107, 'Californium', 'californium', 'Californium', NULL, 'Spawn Digital', '2016-02-16 00:00:00+00', 'https://philipdick.com/images/covers/Californium.jpg', 11, 'Explore the worlds of Californium, a first person exploration game where you are a writer trapped into shifting realities. Will you find what’s behind the simulacra ? Berkeley, 1967. You are Elvin Green, a writer whose career is not better than his sentimental life. Besides, the day starts badly : your wife Thea left you a break up letter. As for Eddy, your editor, he summons you : "you are a writer who does not write" - you should find yourself another editor. Your world is falling apart. Too much acid and cheap booze ? Too many sleepless nights stuck to your typewriter, powerless to tackle your first novel ? Your poor mental health is collapsing. Luckily this nightmarish world is unstable, you can extract yourself from it and thus access another reality! You have nothing to lose!', NULL, true, '2020-10-23 15:16:38.837+00', '2020-10-23 15:16:38.837+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (108, 'Left Coast', 'left coast', 'Left-Coast', NULL, 'Steve Hickey Games', '2015-04-08 00:00:00+00', 'https://philipdick.com/images/covers/LeftCoast.jpg', 11, 'Left Coast is a role-playing game about a science fiction writer in California, who struggles as the weirdness from her novels spills into real life.', NULL, true, '2020-10-23 15:21:06.62+00', '2020-10-30 03:09:35.672+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (117, 'Pink Beams of Light from the God in the Gutter: The Science-Fictional Religion of Philip K. Dick', 'pink beams of light from the god in the gutter: the science-fictional religion of philip k. dick', 'Pink-Beams-Of-Light-From-The-God-In-The-Gutter-The-Science-Fictional-Religion-Of-Philip-K-Dick', 'Gabriel', 'McKee', '2003-12-30 00:00:00+00', 'https://philipdick.com/images/covers/PinkBeamsOfLightFromTheGodInTheGutter.jpg', 4, 'From his earliest stories, Philip K. Dick''s science fiction had strong religious and philosophical themes. In Pink Beams of Light from the God in the Gutter, Gabriel McKee gives an overview of Dick''s religious experiences and his attempts at communicating them in published works, drawing on Dick''s fiction as well as his private journals and personal correspondence.', NULL, true, '2020-10-28 00:27:06.532+00', '2020-10-28 00:27:06.532+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (109, 'Philip K. Dick: Contemporary Critical Interpretations', 'philip k. dick: contemporary critical interpretations', 'Philip-K-Dick-Contemporary-Critical-Interpretations', 'Samuel J.', 'Umland', '1995-01-24 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickContemporaryCriticalInterpretations.jpg', 4, 'This book contains 11 essays and a comprehensive bibliography. The essays reveal the extent to which Philip K. Dick''s personal obsessions pre-figured postmodernist concerns with humanity''s self-alienation, cultural and personal paranoia, and the politics of simulation, deceit, and self-deception. The contributors reveal how Dick''s ontological concerns, stated in his repeated questioning of What is real?, are also political concerns. Thus, they examine the philosophical and religious foundations on which his work rests, offering much-needed arguments which reveal both his philosophical depth and the extent to which he drew from esoteric and occult religions. His cultural critique also receives significant exposition, as the contributors reveal how Dick''s fiction enacts the larger cultural struggles of cold war America, with its conflicting private visions and public realities, and its personal and political loyalties. The contributors argue for the significance of heretofore neglected or marginalized texts of Dick as well, including in their discussions many early short stories from the early 1950s and neglected novels of the mid-1960s, arguing that there is a need to understand how Dick shaped (or misshaped) his fictions so as to reimagine the life of his society.', NULL, true, '2020-10-27 21:42:48.768+00', '2020-10-27 21:42:48.768+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (110, 'Philip K. Dick: Exhilaration and Terror of the Postmodern', 'philip k. dick: exhilaration and terror of the postmodern', 'Philip-K-Dick-Exhilaration-And-Terror-Of-The-Postmodern', 'Christopher', 'Palmer', '2003-01-07 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickExhilarationAndTerrorOfThePostmodern.jpg', 4, 'Once the sole possession of fans and buffs, the SF author Philip K Dick is now finding a much wider audience, as the success of the films Blade Runner and Minority Report shows. The kind of world he predicted in his funny and frightening novels and stories is coming closer to most of us: shifting realities, unstable relations, uncertain moralities. Philip K Dick: Exhilaration and Terror of the Postmodern examines a wide range of Dick''s work, including his short stories and posthumously published realist novels. Christopher Palmer analyses the puzzling and dazzling effects of Dick''s fiction, and argues that at its heart is a clash between exhilarating possibilities of transformation, and a frightening lack of ethical certainties. Dick''s work is seen as the inscription of his own historical predicament, the clash between humanism and postmodernism being played out in the complex forms of the fiction. The problem is never resolved, but Dick''s ways of imagining it become steadily more ingenious and chal', NULL, true, '2020-10-27 21:47:35.692+00', '2020-10-27 21:48:05.787+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (111, 'Philip K. Dick: Remembering Firebright', 'philip k. dick: remembering firebright', 'Philip-K-Dick-Remembering-Firebright', 'Tessa B.', 'Dick', '2009-03-26 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickRememberingFirebright.jpg', 14, 'Philip K. Dick''s VALIS trilogy and Exegesis began with a series of startling mystical experiences in February and March of 1974. Find out what happened, and how he explained it, through the eyes of his wife, who lived through it with him. This personal look at Phil''s "pink light" experience and the work that it spawned covers the final ten years of his life, with some background from his earlier years. This edition is expanded- Chapter Three is new.', NULL, true, '2020-10-27 21:51:10.464+00', '2020-10-29 14:51:07.943+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (112, 'Philip K. Dick: Revised and Updated (Pocket Essential Series)', 'philip k. dick: revised and updated (pocket essential series)', 'Philip-K-Dick-Revised-And-Updated-Pocket-Essential-Series', 'Andrew M.', 'Butler', '2007-09-28 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickRevisedAndUpdatedPocketEssentialSeries.jpg', 4, 'With a reputation that is still rising as the world catches up with the prodigious outpouring of his imagination, and Hollywood repeatedly raiding his stories—Blade Runner, Minority Report, A Scanner Darkly—Philip K. Dick remains an intriguing literary and cultural figure. At a time when most science fiction was about cowboys in outer space, Dick explored the landscapes of the mind, conjured fake realities, and was able to make readers believe six impossible things before breakfast. Perhaps best-known for Do Androids Dream of Electric Sheep?, he embodied the counterculture a decade before the 1960s. This fully revised and updated look at Dick''s world is a glimpse into a reality where psychiatrists come in suitcases, God speaks through cat food commercials and comes in a handy aerosol can, and where you might be a figment of someone else''s imagination. This pocket-sized volume reviews and analyzes each of Dick''s novels and provides a listing of other books and articles which have grappled with this genius.', NULL, true, '2020-10-27 21:54:03.392+00', '2020-11-04 00:31:08.325+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (114, 'Philip K. Dick and Philosophy: Do Androids Have Kindred Spirits?', 'philip k. dick and philosophy: do androids have kindred spirits?', 'Philip-K-Dick-And-Philosophy-Do-Androids-Have-Kindred-Spirits', 'D. E.', 'Wittkower', '2011-10-18 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickAndPhilosophy.jpg', 4, 'Science fiction writer Philip K. Dick (1928-1982) is the giant imagination behind so much recent popular culture--both movies directly based on his writings, such as Blade Runner (based on the novel Do Androids Dream of Electric Sheep?), Total Recall, Minority Report, and The Adjustment Bureau plus cult favorites such as A Scanner Darkly, Imposter, Next, Screamers, and Paycheck and works revealing his powerful influence, such as The Matrix and Inception. With the much anticipated forthcoming publication in 2011 of volume 1 of Exegesis, his journal of spiritual visions and paranoic investigations, Dick is fast becoming a major influence in the world of popular spirituality and occult thinking.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (115, 'Philip K. Dick and the World We Live In', 'philip k. dick and the world we live in', 'Philip-K-Dick-And-The-World-We-Live-In', 'Evan', 'Lampe', '2015-03-13 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickAndTheWorldWeLiveIn.jpg', 4, 'We live in a world that looks increasingly familiar to the worlds described by Philip K. Dick a half century ago. In this book, Lampe explores the multiple ways in which the global capitalist society—liquid and uncertain—was foreshadowed in Dick’s novels and stories. Analyzing most of Dick’s works, including the often underappreciated stories and early novels, Lampe establishes the outline of a general interpretation of Philip K. Dick for our age. This book also goes beyond Dick’s mystical, philosophical, and metaphysical questions and documents his economic, political, and social vision. With chapters on the rise of the surveillance state, technological unemployment, global governance, family, mental illness, new religious movements, consumerism, and urban geography this book presents new ways to read the most important American science fiction writer of the twentieth century.', NULL, true, '2020-10-28 00:21:52.743+00', '2020-10-28 00:21:52.743+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (118, 'The Postmodern Humanism of Philip K. Dick', 'postmodern humanism of philip k. dick, the', 'The-Postmodern-Humanism-Of-Philip-K-Dick', 'Jason', 'Vest', '2009-02-17 00:00:00+00', 'https://philipdick.com/images/covers/ThePostmodernHumanismOfPhilipKDick.jpg', 4, 'From his 1952 short story "Roog" to the novels The Divine Invasion and VALIS, few authors have had as great of an impact in the latter half of the 20th century as Philip K. Dick. In The Postmodern Humanism of Philip K. Dick, Jason Vest explores the work of this prolific, subversive, and mordantly funny science-fiction writer. He examines how Dick adapted the conventions of science fiction and postmodernism to reflect humanist concerns about the difficulties of maintaining identity, agency, and autonomy in the latter half of the 20th century.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (119, 'Postmodern Metanarratives: Blade Runner and Literature in the Age of Image', 'postmodern metanarratives: blade runner and literature in the age of image', 'Postmodern-Metanarratives-Blade-Runner-And-Literature-In-The-Age-Of-Image', 'Décio Torres', 'Cruz', '2014-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PostmodernMetanarratives.jpg', 4, 'Postmodern Metanarratives investigates the relationship between cinema and literature by analyzing the film Blade Runner as a postmodern work that constitutes a landmark of cyberpunk narrative and establishes a link between tradition and the (post)modern.', NULL, true, '2020-10-28 00:35:27.388+00', '2020-10-28 00:35:27.388+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (120, 'The Search for Philip K. Dick', 'search for philip k. dick, the', 'The-Search-For-Philip-K-Dick', 'Anne R.', 'Dick', '1995-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheSearchForPhilipKDick.jpg', 14, 'Offering an intimate perspective on the life of an important, prolific author, this revealing biography uncovers the inner workings of a cult figure through his tumultuous relationship with his third wife. Brilliant and charismatic, Philip K. Dick was known as a loyal friend, father, and husband, as well as a talented science fiction writer. His six-year marriage to the woman he described as the love of his life” and his intellectual equal was full of passionthe meeting of soul mates. But behind the façade of an untroubled life was a man struggling with his demons, unable to trust anyone, and reliant upon his charm to navigate his increasingly dark reality and descent into drugs and madness. Exposing personal details of their married life as well as the ways he continued to haunt her even after their relationship collapsed, Anne Dick provides thorough research combined with personal memories of this mysterious man.', NULL, true, '2020-10-28 00:38:16.177+00', '2020-10-29 14:30:48.555+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (121, 'Conversations with Philip K. Dick', 'conversations with philip k. dick', 'Conversations-With-Philip-K-Dick', 'Tessa B.', 'Dick', '2017-08-12 00:00:00+00', 'https://philipdick.com/images/covers/ConversationsWithPhilipKDick.jpg', 16, 'Sit down and enjoy a glass of wine or a cup of coffee while having a conversation with Philip K. Dick, author of Blade Runner, The man in the High Castle, Total Recall and many more books and stories.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (122, 'My Life on the Edge of Reality', 'my life on the edge of reality', 'My-Life-On-The-Edge-Of-Reality', 'Tessa B.', 'Dick', '2011-04-30 00:00:00+00', 'https://philipdick.com/images/covers/MyLifeOnTheEdgeOfReality.jpg', 14, 'By the author of Philip K. Dick: Remembering Firebright, this personal memoir tells the life story of a survivor of abuse who also experienced strange and bizarre events that seem to coalesce around a secret government-sponsored experiment in mind control. "The man told me that they were dropping me from the program because I was neither physically nor mentally fit for long-term space travel. I begged him to give me a chance, promising that I would work very hard, but he only looked sad while telling me that it was impossible. He added that I was remembering too much, and that I might tell the secrets to people who shouldn’t know about the program. So now I’m telling the secrets." This second edition has an added Epilogue and cover art by Nick Buchanan.', NULL, true, '2020-10-28 00:49:38.266+00', '2020-10-29 14:50:53.598+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (123, 'Blade Runner Creator Philip K. Dick', 'blade runner creator philip k. dick', 'Blade-Runner-Creator-Philip-K-Dick', 'Tessa B.', 'Dick', '2018-06-20 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerCreatorPhilipKDick.jpg', 14, 'Blade Runner 2049 shows us a world devastated by war and threatened by a power mad creator of artificial humans. This sequel to the 1982 film Blade Runner suffered from a misguided marketing campaign and contradictions in the plot lines, but it succeeds in showing us the stark beauty of scarred landscapes and deteriorating city streets. Humans live alongside replicants, and they buy virtual friends to combat loneliness. Behind the slick facade of brightly lit animated billboards, many people live in poverty in the future city of Los Angeles, which reflects the state of things today. The story began with a novel by an obscure science fiction author who lived in poverty for most of his life. His dark vision of the future became widely known after Ridley Scott''s Blade Runner was released in 1982, but sadly Philip K. Dick died before the film was shown in theatres. His genius is finally recognized, but it is too late for him.', NULL, true, '2020-10-28 00:57:54.303+00', '2020-10-29 14:50:37.795+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (124, 'The Twisted Worlds of Philip K. Dick: A Reading of Twenty Ontologically Uncertain Novels', 'twisted worlds of philip k. dick: a reading of twenty ontologically uncertain novels, the', 'The-Twisted-Worlds-Of-Philip-K-Dick-A-Reading-Of-Twenty-Ontologically-Uncertain-Novels', 'Umberto', 'Rossi', '2011-04-05 00:00:00+00', 'https://philipdick.com/images/covers/TheTwistedWorldsOfPhilipKDick.jpg', 4, 'Philip K. Dick was one of the most popular science fiction novelists of the 20th century, but the contradictory and wily writer has troubled critics who attempt encompassing explanations of his work. This book examines Dick''s writing through the lens of ontological uncertainty, providing a comparative map of his oeuvre, tracing both the interior connections between books and his allusive intertextuality. Topics covered include time travel, alternate worlds, androids and simulacra, finite subjective realities and schizophrenia. Twenty novels are explored in detail, including titles that have received scant critical attention. Some of his most important short stories and two of his realist novels are also examined, providing a general introduction to Dick''s body of work.', NULL, true, '2020-10-28 01:05:18.205+00', '2020-10-28 01:05:18.205+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (125, 'Understanding Philip K. Dick', 'understanding philip k. dick', 'Understanding-Philip-K-Dick', 'Eric Carl', 'Link', '2009-12-01 00:00:00+00', 'https://philipdick.com/images/covers/UnderstandingPhilipKDick.jpg', 14, 'Author of more than forty novels and myriad short stories over a three-decade literary career, Philip K. Dick (1928–1982) single-handedly reshaped twentieth-century science fiction. His influence has only increased since his death with the release of numerous feature films based on his work, including Blade Runner (based on Do Androids Dream of Electric Sheep?), Total Recall (based on "We Can Remember It for You Wholesale"), Minority Report (based on "The Minority Report"), and Next (based on "The Golden Man"). In Understanding Philip K. Dick, Eric Carl Link introduces readers to the life, career, and work of this groundbreaking, prolific, and immeasurably influential force in American literature, media culture, and contemporary science fiction.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (126, 'What If Our World Is Their Heaven? The Final Conversations of Philip K. Dick', 'what if our world is their heaven? the final conversations of philip k. dick', 'What-If-Our-World-Is-Their-Heaven-The-Final-Conversations-Of-Philip-K-Dick', 'Gwen', 'Lee', '2001-01-15 00:00:00+00', 'https://philipdick.com/images/covers/WhatIfOurWorldIsTheirHeaven.jpg', 16, 'In the field of science fiction, the work of Philip K Dick is unparalleled. His novel Do Androids Dream of Electric Sheep? became the classic science-fiction film Blade Runner. His short story, "The Minority Report," was adapted for the screen by Stephen Spielberg and stars Tom Cruise. Dick’s appeal and influence has reached the world over, creating the standard for the literary science fiction novel. In November of 1982, six months before the author''s death, journalist Gwen Lee recorded the first of several in-depth discussions with Philip K. Dick that continued over the course over the next three months. It is a must-read for anyone interested in the field of science fiction.', NULL, true, '2020-10-28 01:38:52.462+00', '2020-10-29 14:30:33.025+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (127, 'The World According to Philip K. Dick', 'world according to philip k. dick, the', 'The-World-According-To-Philip-K-Dick', 'Alexander Dunst,', 'Stefan Schlensag', '2015-04-24 00:00:00+00', 'https://philipdick.com/images/covers/TheWorldAccordingToPhilipKDick.jpg', 4, 'As the first essay collection dedicated to Philip K. Dick in two decades, this volume breaks new ground in science fiction scholarship and brings innovative critical perspectives to the study of one of the twentieth century''s most influential authors.', NULL, true, '2020-10-28 01:42:39.488+00', '2020-10-28 17:36:07.382+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (128, 'High Weirdness: Drugs, Esoterica, and Visionary Experience in the Seventies', 'high weirdness: drugs, esoterica, and visionary experience in the seventies', 'High-Weirdness-Drugs-Esoterica-And-Visionary-Experience-In-The-Seventies', 'Erik', 'Davis', '2019-06-11 00:00:00+00', 'https://philipdick.com/images/covers/HighWeirdnessDrugsEsotericaAndVisionaryExperienceInTheSeventies.jpg', 4, 'An exploration of the emergence of a new psychedelic spirituality in the work of Philip K. Dick, Terence McKenna, and Robert Anton Wilson.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (129, 'Blade Runner (BFI Film Classics)', 'blade runner (bfi film classics)', 'Blade-Runner-BFI-Film-Classics', 'Scott', 'Bukatman', '2012-09-04 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerBFIFilmClassics.jpg', 5, 'Ridley Scott''s dystopian classic Blade Runner, an adaptation of Philip K. Dick''s novel, Do Androids Dream of Electric Sheep?, combines noir with science fiction to create a groundbreaking cyberpunk vision of urban life in the twenty-first century. With replicants on the run, the rain-drenched Los Angeles which Blade Runner imagines is a city of oppression and enclosure, but a city in which transgression and disorder can always erupt. Graced by stunning sets, lighting, effects, costumes and photography, Blade Runner succeeds brilliantly in depicting a world at once uncannily familiar and startlingly new.   In his innovative and nuanced reading, Scott Bukatman details the making of Blade Runner and its steadily improving fortunes following its release in 1982. He situates the film in terms of debates about postmodernism, which have informed much of the criticism devoted to it, but argues that its tensions derive also from the quintessentially twentieth-century, modernist experience of the city – as a space both imprisoning and liberating.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (130, 'Blade Runner 2049: The Complete Screenplays', 'blade runner 2049: the complete screenplays', 'Blade-Runner-2049-The-Complete-Screenplays', 'David', 'Bolton', '2020-06-18 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049TheCompleteScreenplays.jpg', 5, 'Thirty years after the events of Blade Runner (1982), a new Blade Runner, L.A.P.D. Officer "K" (Ryan Gosling), unearths a long-buried secret that has the potential to plunge what''s left of society into chaos. K''s discovery leads him on a quest to find Rick Deckard (Harrison Ford), a former L.A.P.D. Blade Runner, who has been missing for thirty years.', NULL, true, '2020-10-28 15:36:42.305+00', '2020-10-28 15:55:38.124+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (131, 'Blade Runner: Screenplay', 'blade runner: screenplay', 'Blade-Runner-Screenplay', 'Terrence', 'Ryan', '2020-06-02 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerScreenplay.jpg', 5, 'Deckard (Harrison Ford) is forced by the police Boss (M. Emmet Walsh) to continue his old job as Replicant Hunter. His assignment: eliminate four escaped Replicants from the colonies who have returned to Earth. Before starting the job, Deckard goes to the Tyrell Corporation and he meets Rachel (Sean Young), a Replicant girl he falls in love with.', NULL, true, '2020-10-28 15:47:46.58+00', '2020-10-28 15:47:46.58+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (135, 'Blade Runner 2049 - Interlinked - The Art', 'blade runner 2049 - interlinked - the art', 'Blade-Runner-2049-Interlinked-The-Art', 'Tanya', 'Lapointe', '2020-08-11 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049InterlinkedTheArt.jpg', 5, 'The official art book for Blade Runner 2049 and a companion volume to The Art and Soul of Blade Runner 2049.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (136, 'The Movie Art of Syd Mead: Visual Futurist', 'movie art of syd mead: visual futurist, the', 'The-Movie-Art-Of-Syd-Mead-Visual-Futurist', 'Syd', 'Mead', '2017-09-19 00:00:00+00', 'https://philipdick.com/images/covers/TheMovieArtOfSydMeadVisualFuturist.jpg', 5, 'Syd Mead is one of the most accomplished and widely respected artists and industrial designers alive today. His career boasts an incredible array of projects from designing cars to drafting architectural renderings, but he is most famous for his work as a concept artist on some of the most visually arresting films in the history of cinema. Since working on Star Trek: The Motion Picture in 1978 as a production illustrator Syd Mead has always aimed to render “reality ahead of schedule,” creating evocative designs that marry believable content with a neofuturistic form. It is this ability to predict technological potential that has helped Mead create such a distinctive and influential aesthetic. From his work with Ridley Scott on Blade Runner, to his striking designs for the light cycles in Tron, to his imposing concept art for the U.S.S. Sulaco in James Cameron’s Aliens, Syd Mead has played a pivotal role in shaping cinema’s vision of the future. 
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (137, 'The Art and Soul of Blade Runner 2049', 'art and soul of blade runner 2049, the', 'The-Art-And-Soul-Of-Blade-Runner-2049', 'Tanya', 'Lapointe', '2017-10-06 00:00:00+00', 'https://philipdick.com/images/covers/TheArtAndSoulOfBladeRunner2049.jpg', 5, 'In the 1982''s groundbreaking science-fiction film Blade Runner, the world saw a vision of the future so bold and breathtaking that it still remains at the forefront of pop culture. Thirty-five years later, many of the world-renowned original cast and crew made a thrilling return to that world. Their journey is chronicled with captivating detail in this official visual guide to Blade Runner 2049, presented in an oversized, luxe, full-colour format. The author documented the film''s production for two years, gaining unprecedented access to the creative process of bringing this epic to life. Exclusive concept art, storyboards, behind-the-scenes photography, and production stills are accompanied by fascinating insights from the cast and crew. The Art and Soul of Blade Runner 2049 is both a display-worthy art piece and a mesmerising insider''s look at the unforgettable characters, iconic environments, and unbelievable technology featured in the film.', NULL, true, '2020-10-28 16:17:09.198+00', '2020-10-28 16:17:09.198+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (138, 'Blade Runner 2049: The Storyboards', 'blade runner 2049: the storyboards', 'Blade-Runner-2049-The-Storyboards', 'Sam', 'Hudecki', '2021-04-06 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049TheStoryboards.jpg', 5, 'Blade Runner 2049: The Storyboards is the only book presenting all of the storyboards from the film.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (139, 'Blade Runner: The Inside Story', 'blade runner: the inside story', 'Blade-Runner-The-Inside-Story', 'Don', 'Shay', '2003-06-01 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerTheInsideStory.jpg', 5, 'In 1982, to coincide with Blade Runner''s original release, Cinefex, the respected magazine devoted to movie design and special effects devoted an entire, extended issue to Ridley Scott''s sci-fi masterpiece. That issue has been out of print since then, but in constant demand  copies now sell on the collector''s market for over $100. Titan Books is proud to bring this classic back into print, in a remastered hardcover edition.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (140, 'Los Angeles 2019: The City of Blade Runner', 'los angeles 2019: the city of blade runner', 'Los-Angeles-2019-The-City-Of-Blade-Runner', 'Oliver', 'Carmi', '2020-07-04 00:00:00+00', 'https://philipdick.com/images/covers/LosAngeles2019TheCityOfBladeRunner.jpg', 5, 'Blade Runner is more than a film, it is a universe. From a distance, the city appears to be a living hell, forever sprawling in the dark horizon. As in a Dantesque vision, slowly rising up towards the heights of the sky, the spaces become purer, cleaner, emptier, in what seems a heavenly ascent. However, only after a closer look, in the crevices of the textured reality that Los Angeles 2019 really is, do we realize that everything is upside down. An architectural and social analysis of the spaces of the city of Blade Runner.', NULL, true, '2020-10-28 16:29:20.817+00', '2020-10-28 16:29:20.817+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (179, 'The Collected Works of Philip K. Dick', 'collected works of philip k. dick, the', 'The-Collected-Works-Of-Philip-K-Dick', 'Philip K.', 'Dick', '2012-09-05 00:00:00+00', 'https://philipdick.com/images/covers/TheCollectedWorksOfPhilipKDick.jpg', 2, '"The Collected Works of Philip K. Dick" is a collection of 11 science fiction stories by Philip K. Dick. Included in this book:
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (141, 'Everything Seems Significant', 'everything seems significant', 'Everything-Seems-Significant', 'Jan', 'Bottiglieri', '2019-07-18 00:00:00+00', 'https://philipdick.com/images/covers/EverythingSeemsSignificant.jpg', 5, '“Everything Seems Significant sails and embraces... such a deep, kaleidoscopic dive it takes. This is brilliant, inspired work. So much has been written about Blade Runner, but none of it penetrates like this. The spell of it all is distilled and caught in the sly, prescient grip of Bottiglieri''s poems.”
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (142, 'Blade Runner 2049 and Philosophy', 'blade runner 2049 and philosophy', 'Blade-Runner-2049-And-Philosophy', 'Robin Bunce,', 'Trip McCrossin', '2019-08-20 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049AndPhilosophy.jpg', 5, 'Blade Runner 2049 is a 2017 sequel to the 1982 movie Blade Runner, about a world in which some human-looking replicants have become dangerous, so that other human-looking replicants, as well as humans, have the job of hunting down the dangerous models and “retiring” (destroying) them. Both films have been widely hailed as among the greatest science-fiction movies of all time, and Ridley Scott, director of the original Blade Runner, has announced that there will be a third Blade Runner movie.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (143, 'Philip K. Dick (NBM Comics Biographies)', 'philip k. dick (nbm comics biographies)', 'Philip-K-Dick-NBM-Comics-Biographies', 'Laurent Queyssi,', 'Mauro Marchesi', '2019-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickNBMComicsBiographies.jpg', 17, 'One of the greatest writers in science fiction history, Philip K. Dick is mostly remembered for such works as Blade Runner, Minority Report, and Total Recall. His dark, fascinating work centered on alternate universes and shifting realities in worlds often governed by monopolistic corporations and authoritarian governments. His own life story seems a tussle with reality, cycling through five marriages and becoming increasingly disjointed with fits of paranoia and hallucinations fueled by abuse of drugs meant to stabilize him. His dramatic story is presented unvarnished in this biography.', NULL, true, '2020-10-28 16:43:02.429+00', '2020-11-04 00:38:56.107+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (144, 'The Cyberpunk Nexus: Exploring the Blade Runner Universe', 'cyberpunk nexus: exploring the blade runner universe, the', 'The-Cyberpunk-Nexus-Exploring-The-Blade-Runner-Universe', 'Lou', 'Tambone', '2018-07-13 00:00:00+00', 'https://philipdick.com/images/covers/TheCyberpunkNexusExploringTheBladeRunnerUniverse.jpg', 5, 'In 1982, a new benchmark was set for science-fiction film with the release of Blade Runner. Based on the book Do Androids Dream of Electric Sheep? by the acclaimed novelist Philip K. Dick and directed by Ridley Scott, Blade Runner was a visual and philosophical tour-de-force, set in a dystopian future in which artificially intelligent replicants, nearly indistinguishable from humans, are hunted down by police-operatives known as Blade Runners. Featuring the talents of Harrison Ford, Rutger Hauer, Sean Young, Joanna Cassidy, Edward James Olmos, and Darryl Hannah, the film tackled numerous themes and birthed controversies that have been poured over by fans and critics ever since. Blade Runner has also inspired literary and comic-book spin-offs, and a cinematic sequel released in 2017. The Cyberpunk Nexus: Exploring the Blade Runner Universe examines the entire Blade Runner saga, from the original novel to its numerous film iterations. The book features a foreword by Paul M. Sammon (Future Noir: The Making of Blade Runner). Essayists include Bryce Carlson (of BOOM! Comics’ Sheep adaptation), Paul J. Salamoff, Robert Meyer Burnett, Rich Handley, Zaki Hasan, Julian Darius, and many others, with a cover by popular artist Matt Busch. From Sequart Organization. More info at http://sequart.org', NULL, true, '2020-10-28 16:50:56.583+00', '2020-10-28 16:50:56.583+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (145, 'Blade Runner (Cultographies)', 'blade runner (cultographies)', 'Blade-Runner-Cultographies', 'Matt', 'Hills', '2011-12-13 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerCultographies.jpg', 5, 'More than just a box office flop that resurrected itself in the midnight movie circuit, Blade Runner (1982) achieved extraordinary cult status through video, laserdisc, and a five-disc DVD collector''s set. Blade Runner has become a network of variant texts and fan speculationsa franchise created around just one film. Some have dubbed the movie "classroom cult" for its participation in academic debates, while others have termed it "meta-cult," in line with the work of Umberto Eco. The film has also been called "design cult," thanks to Ridley Scott''s brilliant creation of a Los Angeles in 2019, the graphics and props of which have been recreated by devoted fans. Blade Runner tests the limits of this authenticity and artificiality, challenging the reader to differentiate between classic and flop, margin and mainstream, true cult and its replicants.', NULL, true, '2020-10-28 16:53:53.257+00', '2020-11-04 00:34:26.956+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (200, 'Dangerous Days: Making Blade Runner', 'dangerous days: making blade runner', 'Dangerous-Days-Making-Blade-Runner', 'Charles', 'de Lauzirika', '2007-12-18 00:00:00+00', 'https://philipdick.com/images/covers/DangerousDaysMakingBladeRunner.jpg', 8, 'The definitive three-and-a-half hour documentary about the troubled creation and enduring legacy of the science fiction classic Blade Runner (1982), culled from 80 interviews and hours of never-before-seen outtakes and lost footage.', NULL, true, '2020-10-31 16:50:52.852+00', '2020-10-31 16:58:07.423+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (146, 'Blade Runner 2049: A Philosophical Exploration', 'blade runner 2049: a philosophical exploration', 'Blade-Runner-2049-A-Philosophical-Exploration', 'Timothy Shanahan,', 'Paul Smart', '2019-09-30 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049APhilosophicalExploration.jpg', 5, 'Widely acclaimed upon its release as a future classic, Denis Villeneuve’s Blade Runner 2049 is visually stunning, philosophically profound, and a provocative extension of the story in Ridley Scott’s Blade Runner. Containing specially commissioned chapters by a roster of international contributors, this fascinating collection explores philosophical questions that abound in Blade Runner 2049, including:
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (147, 'Philosophy and Blade Runner', 'philosophy and blade runner', 'Philosophy-And-Blade-Runner', 'Timothy', 'Shanahan', '2014-06-25 00:00:00+00', 'https://philipdick.com/images/covers/PhilosophyAndBladeRunner.jpg', 5, 'Philosophy and Blade Runner explores philosophical issues in the film Blade Runner , including human nature, personhood, identity, consciousness, free will, morality, God, death, and the meaning of life. The result is a novel analysis of the greatest science fiction film of all time and a unique contribution to the philosophy of film.', NULL, true, '2020-10-28 17:01:10.161+00', '2020-10-28 17:01:10.161+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (148, 'The Blade Runner Experience: The Legacy of a Science Fiction Classic', 'blade runner experience: the legacy of a science fiction classic, the', 'The-Blade-Runner-Experience-The-Legacy-Of-A-Science-Fiction-Classic', 'Will', 'Brooker', '2006-02-21 00:00:00+00', 'https://philipdick.com/images/covers/TheBladeRunnerExperience.jpg', 5, 'Since its release in 1982, Ridley Scott''s Blade Runner, based on Philip K. Dick''s novel Do Androids Dream of Electric Sheep?, has remained a cult classic through its depiction of a futuristic Los Angeles; its complex, enigmatic plot; and its underlying questions about the nature of human identity. The Blade Runner Experience: The Legacy of a Science Fiction Classic examines the film in a broad context, examining its relationship to the original novel, the PC game, the series of sequels, and the many films influenced by its style and themes. It investigates Blade Runner online fandom and asks how the film''s future city compares to the present-day Los Angeles, and it revisits the film to pose surprising new questions about its characters and their world.', NULL, true, '2020-10-28 17:04:06.652+00', '2020-10-28 17:04:53.227+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (149, 'Blade Runner (Philosophers on Film)', 'blade runner (philosophers on film)', 'Blade-Runner-Philosophers-On-Film', 'Amy Coplan,', 'David Davies', '2015-04-10 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerPhilosophersOnFilm.jpg', 5, 'Ridley Scott’s Blade Runner is widely regarded as a "masterpiece of modern cinema" and is regularly ranked as one of the great films of all time. Set in a dystopian future where the line between human beings and ‘replicants’ is blurred, the film raises a host of philosophical questions about what it is to be human, the possibility of moral agency and freedom in ‘created’ life forms, and the capacity of cinema to make a genuine contribution to our engagement with these kinds of questions.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (150, 'All Those Moments: Stories of Heroes, Villains, Replicants, and Blade Runners', 'all those moments: stories of heroes, villains, replicants, and blade runners', 'All-Those-Moments-Stories-Of-Heroes-Villains-Replicants-And-Blade-Runners', 'Rutger Hauer,', 'Patrick Quinlan', '2007-04-24 00:00:00+00', 'https://philipdick.com/images/covers/AllThoseMoments.jpg', 5, 'He came to mainstream prominence as a machine more human than his creators in Blade Runner, terrified us as a hitchhiker bent on his own death and the death of anyone who got in his way in The Hitcher, and unforgettably portrayed a lonely king roaming the night as a wolf and pining for the love of a hawk during the day in Ladyhawke.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (151, 'Future Noir: The Making of Blade Runner', 'future noir: the making of blade runner', 'Future-Noir-The-Making-Of-Blade-Runner', 'Paul M.', 'Sammon', '1996-06-12 00:00:00+00', 'https://philipdick.com/images/covers/FutureNoirTheMakingOfBladeRunner.jpg', 5, 'The 1992 release of the "Director''s Cut" only confirmed what the international film cognoscenti have know all along: Ridley Scott''s Blade Runner, based on Philip K. Dick''s brilliant and troubling SF novel Do Androids Dream of Electric Sheep, still rules as the most visually dense, thematically challenging, and influential SF film ever made.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (206, 'Total Recall 2070', 'total recall 2070', 'Total-Recall-2070', 'Art', 'Monterastelli', '1999-03-07 00:00:00+00', 'https://philipdick.com/images/covers/TotalRecall2070.jpg', 7, 'A detective for a police agency is teamed with a naive new officer, who is secretly an android, and often conflicts with the Assessor''s Office and the security forces of "the Consortium", a small number of extremely powerful companies.', NULL, true, '2020-11-03 04:15:52.61+00', '2020-11-03 04:20:16.687+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (152, 'Retrofitting Blade Runner: Issues in Ridley Scott''s Blade Runner and Phillip K. Dick''s Do Androids Dream of Electric Sheep?', 'retrofitting blade runner: issues in ridley scott''s blade runner and phillip k. dick''s do androids dream of electric sheep?', 'Retrofitting-Blade-Runner-Issues-In-Ridley-Scotts-Blade-Runner-And-Phillip-K-Dicks-Do-Androids-Dream-Of-Electric-Sheep-', 'Judith B.', 'Kerman', '1997-06-01 00:00:00+00', 'https://philipdick.com/images/covers/RetrofittingBladeRunner.gif', 5, 'This book of essays looks at the multitude of texts and influences which converge in Ridley Scott’s film Blade Runner, especially the film’s relationship to its source novel, Philip K. Dick’s Do Androids Dream of Electric Sheep? Essays consider political, moral and technological issues raised by the film, as well as literary, filmic, technical and aesthetic questions. Contributors discuss the film’s psychological and mythic patterns, importance political issues and the roots of the film in Paradise Lost, Frankenstein, detective fiction, and previous science fiction cinema.', NULL, true, '2020-10-28 17:28:07.113+00', '2020-11-04 00:29:55.404+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (153, 'Studying Blade Runner: Instructor''s Edition', 'studying blade runner: instructor''s edition', 'Studying-Blade-Runner-Instructors-Edition', 'Sean', 'Redmond', '2008-08-08 00:00:00+00', 'https://philipdick.com/images/covers/StudyingBladeRunnerInstructor''sEdition.jpg', 5, 'Ridley Scott''s Blade Runner is now widely recognised as a masterwork of science fiction cinema,and one of the most influential of any films released in the last twenty-five years. In Studying Blade Runner, Sean Redmond uses the key concepts of media and film studies―film language, representation, institutions, and audiences―to explore the significance of the film: its breakthrough use of special effects as a narrative tool; the film''s revolutionary representation of the future city; its treatment of racial and sexual politics; its unique status as a text whose meaning was fundamentally altered in its rereleased "Director''s Cut" form―and what this means in an institutional context.', NULL, true, '2020-10-28 17:30:39.915+00', '2020-11-04 00:29:26.112+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (154, 'Blade Runner and the Cinema of Philip K. Dick', 'blade runner and the cinema of philip k. dick', 'Blade-Runner-And-The-Cinema-Of-Philip-K-Dick', 'Jeremy Mark', 'Robinson', '2012-01-23 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerAndTheCinemaOfPhilipKDick.jpg', 4, 'Revised and updated, with new illustrations This book is about the films made from the fiction of Philip K. Dick, which include the classic movie Blade Runner, the Arnold Schwarzenegger actioner Total Recall, Minority Report, directed by Steven Spielberg, and 2007’s Next. A thorough exploration of Blade Runner forms the core of the book, looking at the conception, production, themes and influence of the 1982 Warner Brothers film in every detail. Philip Kindred Dick (1928-1982) was a key figure in 20th century science fiction, famous for embracing drugs and the counter-culture in his work. Dick''s fiction includes The Man In the High Castle, Flow My Tears, The Policeman Said, A Scanner Darkly, The Game Players of Titan, Clan of the Alphane Moon, The Three Stigmata of Palmer Eldritch, Do Androids Dream of Electric Sheep?, Valis, The Divine Invasion, Martian Time-Slip, The Minority Report, and We Can Remember It For You Wholesale. Dick''s themes included perception and reality, drugs, state control, global capitalism, surveillance, and paranoia. Four films are explored here: Blade Runner, Total Recall, Minority Report and A Scanner Darkly (in a chapter by Thomas Christie). The other films based on Phil Dick''s fiction, which are discussed in the final chapter, include Confessions d''un Barjo (a French movie based on Confessions of a Crap Artist), a Canadian film, Screamers, based on Dick''s Second Variety story, Paycheck, directed by John Woo, Next (Lee Tamahori, 2007), based on The Golden Man, and Impostor (Gary Fleder, 2002). The more recent cycle of Philip Kindred Dick movies began with Minority Report and Impostor in 2002 - Paycheck and Next followed in 2003 and 2007, and The Owl In Daylight, a possible film about Dick, Radio Free Alemuth (2008), and The Adjustment Bureau in 2011. A sequel to Screamers was released in 2009, again shooting in Canada. A striking aspect of the films made from the fiction of Philip K. Dick is how many are very high budget movies (Paycheck, Minority Report, Blade Runner, Next and Total Recall), and just how many high profile filmmakers have been attracted to Dick''s stories: Ridley Scott, Paul Verhoeven, John Woo and the most successful film director of recent times, Steven Spielberg. Those four directors together have generated billions globally. Fully illustrated, with a newly revised text for this edition. Bibliography, filmography and notes. ISBN 971861713568. Also available in hardback www.crmoon.com FROM THE FOREWORD: In this comprehensive book, Jeremy Robinson explores the themes of Blade Runner with his usual insight and knowledge of visual and narrative film. Robinson presents a critical and objective outside viewpoint. He tries to be balanced, and to offer criticism as well as praise. It is ultimately important to note that he is writing about art, not the artist, and he admires Ridley Scott as much as I do. Blade Runner has been analyzed, debated, dissected and discussed extensively over the last 25 years and I hope you find Jeremy Robinson''s exploration into Ridley Scott''s seminal sci-fi film noir masterpiece to be innovative and glowing with new ideas that stimulate your imagination and jump start your synapses. (Sheena Duggal, Visual Effects Supervisor, Sony Pictures Imageworks) CONTENTS Foreword Introduction 1 The Cinema of Philip K. Dick 2 Blade Runner 3 Total Recall 4 Steven Spielberg Meets Philip K. Dick: Minority Report 5 A Scanner Darkly 6 Other Philip K. Dick Films Illustrations Bibliography', NULL, true, '2020-10-28 17:33:20.918+00', '2020-10-28 17:33:20.918+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (155, 'How to Build an Android: The True Story of Philip K. Dick''s Robotic Resurrection', 'how to build an android: the true story of philip k. dick''s robotic resurrection', 'How-To-Build-An-Android-The-True-Story-Of-Philip-K-Dicks-Robotic-Resurrection', 'David F.', 'Dufty', '2012-06-05 00:00:00+00', 'https://philipdick.com/images/covers/HowToBuildAnAndroid.jpg', 4, 'The stranger-than-fiction story of the ingenious creation and loss of an artificially intelligent android of science-fiction writer Philip K. Dick
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (156, 'Dream Makers', 'dream makers', 'Dream-Makers', 'Charles', 'Platt', '1980-11-01 00:00:00+00', 'https://philipdick.com/images/covers/DreamMakers.jpg', 16, 'Historic interviews with legendary authors including Stephen King, Kurt Vonnegut, Philip K. Dick, Jerry Pournelle and many others. These interviews were generally captured in the writer''s environment and provide fascinating, behind the scenes views of their writing process and their state of mind. New material includes an Introduction and updated Historical Context.', NULL, true, '2020-10-29 00:32:57.773+00', '2020-10-29 14:31:38.799+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (157, 'Philip K. Dick: Electric Shepherd', 'philip k. dick: electric shepherd', 'Philip-K-Dick-Electric-Shepherd', 'Bruce', 'Gillespie', '1975-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickElectricShepherd.jpg', 4, 'Issue 1 of Best of SF Commentary', NULL, true, '2020-10-29 00:41:56.456+00', '2020-10-29 00:41:56.456+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (158, 'The Matrix Control System of Philip K. Dick And The Paranormal Synchronicities', 'matrix control system of philip k. dick and the paranormal synchronicities, the', 'The-Matrix-Control-System-Of-Philip-K-Dick-And-The-Paranormal-Synchronicities', 'Timothy Green Beckley,', 'Sean Casteel', '2017-05-18 00:00:00+00', 'https://philipdick.com/images/covers/TheMatrixControlSystemOfPhilipKDick.jpg', 4, 'BEYOND ANYTHING YOU EVER IMAGINED WAS POSSIBLE!
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (159, 'The Apocalypse of the Reluctant Gnostics: Carl G. Jung and Philip K. Dick', 'apocalypse of the reluctant gnostics: carl g. jung and philip k. dick, the', 'The-Apocalypse-Of-The-Reluctant-Gnostics-Carl-G-Jung-And-Philip-K-Dick', 'Stuart', 'Douglas', '2018-04-22 00:00:00+00', 'https://philipdick.com/images/covers/TheApocalypseOfTheReluctantGnostics.jpg', 4, 'This book presents a comparison of the Gnostic worldviews of Carl G. Jung and science-fiction author, Philip K. Dick, two figures who have done far more than most to revive an interest in the Gnostic tradition in the modern world. Despite profoundly different approaches - one was a depth psychologist whose unique insights and approach to psychology forced him to explore the depths of the unconscious in a way that inevitably led him to touch frequently on metaphysical or spiritual matters; the other was an author of science-fiction - there are some striking parallels between their unique Gnostic visions.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (160, 'Philip K. Dick: Essays of the Here and Now', 'philip k. dick: essays of the here and now', 'Philip-K-Dick-Essays-Of-The-Here-And-Now', 'David Sandner, Donald E. Palumbo,', 'C.W. Sullivan III', '2020-04-11 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickEssaysOfTheHereAndNow.jpg', 4, 'Philip K. Dick was a visionary writer of science fiction. His works speak to contemporary fears of being continually watched by technology, and the paranoia of modern life in which we watch ourselves and lose our sense of identity. Since his death in 1982, Dick''s writing remain frighteningly relevant to 21st century audiences. Dick spent his life in near poverty and it was only after his death that he gained popular and critical recognition. In this new collection of essays, interviews, and talks, Philip K Dick is rediscovered. Concentrating both on recent critical studies and on reassessing his legacy in light of his new status as a "major American author," these essays explore, just what happened culturally and critically to precipitate his extraordinary rise in reputation. The essays look for his traces in the places he lived, in the SF community he came from, and in his influence on contemporary American literature and culture, and beyond.', NULL, true, '2020-10-29 01:52:37.592+00', '2020-10-29 01:52:37.592+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (161, 'The Philosophy of Science Fiction: Henri Bergson and the Fabulations of Philip K. Dick', 'philosophy of science fiction: henri bergson and the fabulations of philip k. dick, the', 'The-Philosophy-Of-Science-Fiction-Henri-Bergson-And-The-Fabulations-Of-Philip-K-Dick', 'James Edward', 'Burton', '2015-09-24 00:00:00+00', 'https://philipdick.com/images/covers/ThePhilosophyOfScienceFiction.jpg', 4, 'The Philosophy of Science Fiction: Henri Bergson and the Fabulations of Philip K. Dick explores the deep affinity between two seemingly quite different thinkers, in their attempts to address the need for salvation in (and from) an era of accelerated mechanization, in which humans'' capacity for destroying or subjugating the living has attained a planetary scale.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (162, 'Robots, Androids, and Mechanical Oddities: The Science Fiction of Philip K. Dick', 'robots, androids, and mechanical oddities: the science fiction of philip k. dick', 'Robots-Androids-And-Mechanical-Oddities-The-Science-Fiction-Of-Philip-K-Dick', 'Philip K.', 'Dick', '1984-06-01 00:00:00+00', 'https://philipdick.com/images/covers/RobotsAndroidsAndMechanicalOddities.jpg', 2, 'What is human? What is a machine? How do they differ? Or do they?
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (163, 'Science Fiction: An Oral History', 'science fiction: an oral history', 'Science-Fiction-An-Oral-History', 'D. Scott', 'Apel', '2014-05-04 00:00:00+00', 'https://philipdick.com/images/covers/ScienceFictionAnOralHistory.jpg', 16, '"Science Fiction: An Oral History" features in-depth dialogs with prominent authors of the genre''s Golden Age (1930-1960) and New Wave (1960-1980) eras, including Philip K. Dick, Theodore Sturgeon, Fritz Leiber, Roger Zelazny, C.L. Moore, Leigh Brackett, Norman Spinrad and Robert Anton Wilson. Through these interviews with some of the most highly-regarded figures in the field, we get first-hand insight into the changes in style, subject and society that shaped the evolution of the genre over its first half-century. Topics covered include their influences and working habits, analysis of their major works, discussions of science fiction as the modern mythology and its differences from mainstream fiction, as well as numerous anecdotes from their professional and personal lives. Far from a dry history of a dynamic subject, the interview format captures the spontaneity, enthusiasm and humor of spending a couple hours in good conversation with an old friend. Now includes a bonus interview with "Star Trek" creator Gene Roddenberry.', NULL, true, '2020-10-29 02:01:48.032+00', '2020-10-29 14:31:06.353+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (164, 'Philip K. Dick: The Dream Connection', 'philip k. dick: the dream connection', 'Philip-K-Dick-The-Dream-Connection', 'D. Scott', 'Apel', '1987-07-04 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickTheDreamConnection.jpg', 4, 'This exceptional anthology includes over eight hours of interviews with noted science fiction author Philip K. Dick, including the most complete and personal account of his March, 1974, "mystical experiences," plus numerous supplementary essays, including Robert Anton Wilson on PKD''s mystical experiences, R. Faraday Nelson on collaborating with PKD, a rarely-seen short story in which PKD fictionalizes his mystical experiences, and much more.', NULL, true, '2020-10-29 02:05:37.437+00', '2020-10-29 02:05:37.437+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (165, 'A Life of Philip K. Dick: The Man Who Remembered the Future', 'life of philip k. dick: the man who remembered the future, a', 'A-Life-Of-Philip-K-Dick-The-Man-Who-Remembered-The-Future', 'Anthony', 'Peake', '2013-09-25 00:00:00+00', 'https://philipdick.com/images/covers/ALifeOfPhilipKDickTheManWhoRememberedTheFuture.jpg', 14, 'Philip K. Dick was a hugely influential writer who drew upon his own life to address the nature of drug abuse, paranoia, schizophrenia, and transcendental experiences of all kinds. He was a prolific author and many of his books were turned into popular films such as Blade Runner, A Scanner Darkly, Total Recall, and Minority Report. This book has been written with the cooperation of several close acquaintances and looks to examine his work as well as the socio-political-cultural environment in which he lived. It will be of great interest to any fan of Philip K. Dick or science fiction in general, as well as anyone who grew up the 1950s, 1960s, and 1970s.', NULL, true, '2020-10-29 02:09:04.57+00', '2020-10-29 14:27:58.08+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (166, 'Philip K. Dick (Writers of the 21st Century)', 'philip k. dick (writers of the 21st century)', 'Philip-K-Dick-Writers-Of-The-21st-Century', 'Martin H. Greenberg,', 'Joseph D. Olander', '1983-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickWritersOfThe21stCentury.jpg', 4, 'Analyzes the imagery and the political and philosophical themes of the science fiction novels and short stories of Philip K. Dick', NULL, true, '2020-10-29 02:12:13.341+00', '2020-11-04 00:30:50.291+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (167, 'Philip K. Dick (Twayne''s United States Authors Series)', 'philip k. dick (twayne''s united states authors series)', 'Philip-K-Dick-Twaynes-United-States-Authors-Series', 'Douglas A.', 'Mackey', '1988-04-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickTwaynesUnitedStatesAuthorsSeries.jpg', 4, 'One of science fiction''s most prolific authors, Philip K. Dick is also one of the genre''s most intellectually challenging explorers of the multidimensional subjective nature of reality. In Philip K. Dick, author Douglas A. Mackey has undertaken the formidable task of elucidating the psychological and philosophical issues that permeate all of Dick''s work and that, Mackey argues, earn him a place with Borges and Kafka among twentieth-century writers.', NULL, true, '2020-10-29 02:16:49.915+00', '2020-11-04 00:30:44.63+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (168, 'To the High Castle, Philip K. Dick: A Life, 1928-1962', 'to the high castle, philip k. dick: a life, 1928-1962', 'To-The-High-Castle-Philip-K-Dick-A-Life-1928-1962', 'Gregg', 'Rickman', '1989-11-01 00:00:00+00', 'https://philipdick.com/images/covers/ToTheHighCastlePhilipKDickALife.jpg', 14, 'This book is the story of Philip K. Dick, American author, from his birth to the age of 32, and the publication of his most famous novel, The Man In the High Castle. It is the story of a man who more than most authors lived his fiction, it is a story of courage and perseverance in the face of trememdous difficulties. It is a book that says a lot, not only about a fascinating writer and man, but also about American culture at mid-century, and perhaps most importantly about a topic of overwhelming importance: child abuse, it causes and its effects.', NULL, true, '2020-10-29 02:24:49.43+00', '2020-10-29 14:30:42.089+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (169, 'Philip K. Dick: The Last Testament', 'philip k. dick: the last testament', 'Philip-K-Dick-The-Last-Testament', 'Gregg', 'Rickman', '1985-06-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickTheLastTestament.jpg', 16, 'An electric collection of interviews--including the first and the last--with one of the 20th century''s most prolific, influential, and dazzlingly original writers of science fiction
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (170, 'Philip K. Dick: In His Own Words', 'philip k. dick: in his own words', 'Philip-K-Dick-In-His-Own-Words', 'Gregg', 'Rickman', '1988-03-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickInHisOwnWords.jpg', 16, 'Philip K. Dick: In His Own Words is the late science fiction master talking about: all of his books, including VALIA, Ubik, The Man in the High Castle, to name but a few. His views on science fiction and the authors he knew. The creative process of writing his last novel, The Transmigration of Timothy Archer. His thoughts on the film Blade Runner made from his novel, Do Androids Dream of Electric Sheep? Phillip K. Dick: In His Own Words is book one of a three book series of Phillip K. Dicks''s life and work by Gregg Rickman. Rickman conducted extensive interviews with Dick before his untimely death in 1982.', NULL, true, '2020-10-29 02:31:34.429+00', '2020-10-29 14:29:54.732+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (171, 'On Philip K. Dick: 40 Articles from Science-Fiction Studies', 'on philip k. dick: 40 articles from science-fiction studies', 'On-Philip-K-Dick-40-Articles-From-Science-Fiction-Studies', 'R.D. Mullen, Arthur B. Evans, Veronica Hollinger', 'Istvan Csicsery-Ronay, Jr.', '1992-06-01 00:00:00+00', 'https://philipdick.com/images/covers/OnPhilipKDick40ArticlesFromScienceFictionStudies.jpg', 4, 'Everything on Dick published in Science Fiction Studies, 1975-1992.', NULL, true, '2020-10-29 02:36:15.9+00', '2020-10-29 02:36:15.9+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (172, 'Mind in Motion: The Fiction of Philip K. Dick', 'mind in motion: the fiction of philip k. dick', 'Mind-In-Motion-The-Fiction-Of-Philip-K-Dick', 'Patricia S.', 'Warrick', '1987-09-01 00:00:00+00', 'https://philipdick.com/images/covers/MindInMotionTheFictionOfPhilipKDick.jpg', 4, 'Discussed here are the eight novels that Patricia Warrick considers representative of Dick’s finest writing—the works that will become classics, including The Man in the High Castle and Do Androids Dream of Electric Sheep?
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (173, 'The Novels of Philip K. Dick', 'novels of philip k. dick, the', 'The-Novels-Of-Philip-K-Dick', 'Kim Stanley', 'Robinson', '1984-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheNovelsOfPhilipKDick.jpg', 4, '"The Novels Of Philip K. Dick: Studies in Speculative Fiction" was written by Kim Stanley Robinson as his thesis for his Ph.D. in English literature. It is a literary review of the works of the acclaimed American science fiction writer, author of "The Man In The High Castle," "Do Androids Dream Of Electric Sheep," "VALIS," and many others. Robinson himself is a widely known and well-respected writer of speculative fiction.', NULL, true, '2020-10-29 02:43:12.77+00', '2020-10-29 02:43:12.77+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (174, 'Flow My Tears, the Policeman Said', 'flow my tears, the policeman said', 'Flow-My-Tears-The-Policeman-Said-Play', 'Linda', 'Hartinian', '1985-06-18 00:00:00+00', 'https://philipdick.com/images/covers/FlowMyTearsThePolicemanSaidPlay.jpg', 13, 'In his New York Times review Mel Gussow wrote, "In Flow My Tears, the Policeman Said, Jason Taverner, a celebrated television and singing star, wakes up one morning to discover that his identity has been obliterated. Nobody knows his fame and all computer data on him have been purged...Taverner has been magically transported to a parallel world, a favorite device of the late science-fiction author." America has become a police state and Taverner could be arrested and sent to a labor camp. Felix Buckman, the policeman of the title, is convinced Taverner is plotting against the authority of the state—why else would he destroy all documentation of his identity? This intense, bizarre, and fast-paced work explores identity and the recognition of love, loss and grief.', NULL, true, '2020-10-29 14:55:23.835+00', '2020-11-04 00:20:50.12+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (175, 'Young Authors'' Club: The Wartime Adolescent Writings of Philip K. Dick', 'young authors'' club: the wartime adolescent writings of philip k. dick', 'Young-Authors-Club-The-Wartime-Adolescent-Writings-Of-Philip-K-Dick', 'Philip K.', 'Dick', '2014-12-05 00:00:00+00', 'https://philipdick.com/images/covers/YoungAuthorsClub.jpg', 2, 'The first complete collection of Philip K. Dick''s wartime adolescent writings in the Berkeley Daily Gazette (1942-1944); Edited with an Introduction by Frank T. Hollander', NULL, true, '2020-10-29 15:00:14.821+00', '2020-10-29 15:00:14.821+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (176, 'Philip K. Dick (Starmont Reader''s Guide Series)', 'philip k. dick (starmont reader''s guide series)', 'Philip-K-Dick-Starmont-Readers-Guide-Series', 'Hazel', 'Pierce', '1982-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickStarmontReadersGuideSeries.jpg', 4, 'I. Chronology
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (177, 'The Early Stories of Philip K. Dick', 'early stories of philip k. dick, the', 'The-Early-Stories-Of-Philip-K-Dick', 'Philip K.', 'Dick', '2014-05-27 00:00:00+00', 'https://philipdick.com/images/covers/TheEarlyStoriesOfPhilipKDick.jpg', 2, 'Every legend has a beginning. Here, Dreamscape Media presents a collection of short stories penned by Philip K. Dick early in his masterful and legendary writing career. Stories include: Beyond Lies the Wub; Beyond the Door; The Hanging Stranger; Mr. Spaceship; The Gun; Tony and the Beetles; The Eyes Have It; The Variable Man; Second Variety; The Skull; The Crystal Crypt; Piper in the Woods; The Defenders.', NULL, true, '2020-10-29 15:22:32.445+00', '2020-10-29 15:25:20.017+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (178, 'The Skull', 'skull, the', 'The-Skull', 'Philip K.', 'Dick', NULL, 'https://philipdick.com/images/covers/TheSkull.jpg', 12, 'Conger, the protagonist, is given a chance to get out of jail if he agrees to travel back in time and kill a man. He has agreed to kill a stranger he has never seen. He isn''t concerned about getting the wrong man. He knows what the man looked like. There was no way he could make a mistake about his target''s identity -- he has the man''s skull under his shoulder.', 'https://1999pkdweb.philipdick.com/The%20Skull.htm', true, '2020-10-29 15:27:37.701+00', '2020-10-29 15:28:17.075+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (207, 'Minority Report', 'minority report', 'Minority-Report-Television', 'Max', 'Borenstein', '2015-09-21 00:00:00+00', 'https://philipdick.com/images/covers/MinorityReportTelevision.jpg', 7, 'A man haunted by the future and a cop haunted by her past race to stop the worst crimes of the year 2065 before they happen.', NULL, true, '2020-11-03 04:24:32.281+00', '2020-11-04 00:20:13.63+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (180, 'Do Androids Dream of Electric Sheep?', 'do androids dream of electric sheep?', 'Do-Androids-Dream-Of-Electric-Sheep-Graphic-Novel', 'Philip K.', 'Dick', '2015-11-25 00:00:00+00', 'https://philipdick.com/images/covers/DoAndroidsDreamOfElectricSheepOmnibus.jpg', 17, 'What''s to Love: If you loved the 1982 Ridley Scott film Blade Runner, chances are you know it''s based on Philip K. Dick''s novel Do Androids Dream of Electric Sheep? From 2009-2011, we published a 24-issue graphic interpretation of the novel as realized by artist Tony Parker. Now, for the first time, all 24 issues of the Eisner Award-nominated series are collected into one complete omnibus edition, featuring a brand-new cover by Mondo artist Jay Shaw and essays from Jonathan Lethem, Ed Brubaker, Warren Ellis, Matt Fraction, and more.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (181, 'Philip K. Dick''s Electric Ant', 'philip k. dick''s electric ant', 'Philip-K-Dicks-Electric-Ant', 'David', 'Mack', '2010-12-08 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDicksElectricAnt.jpg', 17, 'From the mind of legendary Sci-fi author Philip K. Dick! When Garson Poole wakes up in a hospital room the doctors inform him that they can''t treat him. Because he''s a robot. Specifically, Garson is an Electric Ant, a human-like robot created and programmed to serve a specific function. But what is Garson''s function?', 'https://1999pkdweb.philipdick.com/The%20Electric%20Ant.htm', true, '2020-10-29 15:47:33.837+00', '2020-11-04 00:31:59.405+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (182, 'Philip K. Dick On Film', 'philip k. dick on film', 'Philip-K-Dick-On-Film', 'Gregg', 'Rickman', '2018-08-03 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickOnFilm.jpg', 4, 'The now legendary writings of Philip K. Dick (1928-1982) have inspired fourteen feature films and four television series, none of which he ever saw. Many of them have been widely seen and highly influential; they include Blade Runner, Total Recall, Minority Report, The Man in the High Castle and most recently Blade Runner 2049 and Philip K. Dick''s Electric Dreams. Borrowing his inspired ideas, few of them really utilise the author''s unique imagination and the thought behind them. This book reviews all of the above films and television shows from, as much as possible, the perspective of Philip Dick himself. For most of his life a housebound scrivener of pulp science fiction, Dick nonetheless had distinct ideas about movies, and how his work should be filmed. This book incorporates his thought about visual media into a study of how a commercial system of film production grinds an insomniac''s electric sheep into sausages for the masses--some of them, it must be granted, rather tasty. Like Philip K. Dick, Gregg Rickman likes cats, has spent most of his life in California, and will stay up all night writing. Unlike Philip Dick, he doesn''t take snuff and is not a visionary genius.', NULL, true, '2020-10-30 03:16:03.857+00', '2020-10-30 03:16:03.857+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (183, 'Welcome to Reality: The Nightmares of Philip K. Dick', 'welcome to reality: the nightmares of philip k. dick', 'Welcome-To-Reality-The-Nightmares-Of-Philip-K-Dick', 'Uwe', 'Anton', '1991-02-28 00:00:00+00', 'https://philipdick.com/images/covers/WelcomeToRealityTheNightmaresOfPhilipKDick.jpg', 2, '5 • Introduction • essay by Uwe Anton (trans. of Philip K. Dicks Halbleben: Ein Vorwort 1990)
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (184, 'Cosmogony and Cosmology', 'cosmogony and cosmology', 'Cosmogony-And-Cosmology', 'Philip K.', 'Dick', '1987-01-01 00:00:00+00', 'https://philipdick.com/images/covers/CosmogonyAndCosmology.jpg', 3, 'An essay Dick wrote in January of 1978, and, according to Dick''s literary executor, Paul Williams, represents the most comprehensive exegesis of his body of work.', NULL, true, '2020-10-30 19:02:51.192+00', '2020-10-30 19:42:49.911+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (185, 'Do Androids Dream of Electric Sheep?: Dust To Dust', 'do androids dream of electric sheep?: dust to dust', 'Do-Androids-Dream-Of-Electric-Sheep-Dust-To-Dust', 'Chris', 'Roberson', '2011-03-16 00:00:00+00', 'https://philipdick.com/images/covers/DoAndroidsDreamOfElectricSheepDustToDust.jpg', 17, 'Who hunted androids before Rick Deckard? Taking place immediately after World War Terminus ends, the problems with artificial life - androids - become apparent. The government decides they must become targets, hunted down, but who will do the dirty work? Two men are assigned: Malcolm Reed, a ''special'' human with the power to feel others'' emotions, and Charlie Victor, who''s the perfect man for the job - or is he? Meanwhile Samantha Wu, a Stanford biologist, fights to save the last of the world''s animals. John W. Campbell Award-nominee Chris Roberson writes the prequel to John W. Campbell Memorial Award-winner Philip K. Dick''s Do Androids Dream of Electric Sheep?, one of the greatest science fiction novels ever published!', NULL, true, '2020-10-30 19:17:04.62+00', '2020-10-30 19:43:44.548+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (186, 'The Ganymede Takeover', 'ganymede takeover, the', 'The-Ganymede-Takeover', 'Philip K. Dick,', 'Ray Nelson', '1967-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheGanymedeTakeover.jpg', 1, 'Earth has been invaded - but one human terrorist has discovered a weapon which might change the course of the war!
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (201, 'On the Edge of ''Blade Runner''', 'on the edge of ''blade runner''', 'On-The-Edge-Of-Blade-Runner', 'Andrew', 'Abbott', '2000-07-15 00:00:00+00', 'https://philipdick.com/images/covers/OnTheEdgeOfBladeRunner.jpg', 8, 'About the epic film "Blade Runner", giving insights into it''s history with interviews of Ridley Scott, the writers and nearly all the cast. Interviews with production staff, giving details into the creative process and turmoil during pre-production.', NULL, true, '2020-10-31 16:52:33.801+00', '2020-11-04 00:33:05.801+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (221, 'A Scanner Darkly', 'scanner darkly, a', 'A-Scanner-Darkly-Film', 'Richard', 'Linklater', '2006-07-28 00:00:00+00', 'https://philipdick.com/images/covers/AScannerDarklyFilm.jpg', 6, 'Set in suburban Orange County, California, in a future where America has lost the war on drugs, one reluctant undercover cop (Reeves) is ordered to start spying on his friends.', NULL, true, '2020-11-03 16:34:31.961+00', '2020-11-04 00:19:04.751+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (187, 'A Scanner Darkly', 'scanner darkly, a', 'A-Scanner-Darkly-Graphic-Novel', 'Philip K.', 'Dick', '2006-07-04 00:00:00+00', 'https://philipdick.com/images/covers/AScannerDarklyGraphicNovel.jpg', 17, 'A haunting graphic version of one of Philip K. Dick’s most popular and best-selling novels.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (188, 'Blade Runner 2: The Edge of Human', 'blade runner 2: the edge of human', 'Blade-Runner-2-The-Edge-Of-Human', 'K. W.', 'Jeter', '1995-10-01 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2TheEdgeOfHuman.jpg', 5, 'In 1982 the brilliant science fiction movie Blade Runner was released, and a phenomenon was born.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (189, 'Blade Runner 3: Replicant Night', 'blade runner 3: replicant night', 'Blade-Runner-3-Replicant-Night', 'K. W.', 'Jeter', '1996-10-01 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner3ReplicantNight.jpg', 5, 'The Blade Runner adventure continues in this dark and stylish novel of nonstop futuristic suspense as ex-blade runner Rick Deckard must cross the most dangerous line of all--the line between human and android.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (190, 'Blade Runner 4: Eye and Talon', 'blade runner 4: eye and talon', 'Blade-Runner-4-Eye-And-Talon', 'K. W.', 'Jeter', '2000-12-04 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner4EyeAndTalon.jpg', 5, 'Fully authorised by the estate of Philip K. Dick and written by the author they felt best equipped to take forward the vision of one of the great names in SF, BLADE RUNNER 4: BEYOND ORION combines the dark imagery, paranoia, tension and pace of Dick''s original novel and the cinematic genius of Ridley Scott in a novel that takes the Blade Runner series into a new millennium.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (191, 'Precious Artifacts: A Philip K. Dick Bibliography', 'precious artifacts: a philip k. dick bibliography', 'Precious-Artifacts-A-Philip-K-Dick-Bibliography', 'Henri Wintz,', 'David Hyde', '2012-06-23 00:00:00+00', 'https://philipdick.com/images/covers/PreciousArtifactsAPhilipKDickBibliography.jpg', 15, 'Precious Artifacts is a fully illustrated bibliography of the novels and major story collections of Philip K. Dick published in the USA and UK. It covers almost 60 years of PKD publications with close to 600 editions referenced with full-color cover art. Fans of Philip K. Dick will find herein a trove of information and details on PKD''s books including knowledgeable essays on collecting books, a useful guide to collecting rare editions, a glossary of bibliographic terms and a chronology of PKD''s novels. "This book belongs on the bookshelves of every fan of Philip K. Dick and every rare book collector in the world." - Lord Running Clam', NULL, true, '2020-10-30 19:59:19.028+00', '2020-10-30 19:59:19.028+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (192, 'Precious Artifacts 2: A Philip K. Dick Bibliography - The Short Stories', 'precious artifacts 2: a philip k. dick bibliography - the short stories', 'Precious-Artifacts-2-A-Philip-K-Dick-Bibliography-The-Short-Stories', 'Henri Wintz,', 'David Hyde', '2014-12-11 00:00:00+00', 'https://philipdick.com/images/covers/PreciousArtifacts2APhilipKDickBibliography.jpg', 15, 'In this second volume of Precious Artifacts, David Hyde and Henri Wintz take us on another journey back in time looking at Philip K. Dick’s short story publication history. This fully illustrated bibliography covers 125 short stories written by Philip K. Dick between 1952 and 1981. It is full of details on the stories themselves, the editors who published them, the magazines, anthologies and collections they appeared in, as well as the artists who illustrated them. The bibliography covers not only editions of the books and pulp magazines from the United States and the United Kingdom, but also rare publications from Australia and New Zealand. Precious Artifacts 2 is an invaluable resource for Philip K. Dick fans and scholars.', NULL, true, '2020-10-30 20:08:09.946+00', '2020-10-30 20:08:09.946+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (202, 'The Worlds of Philip K Dick', 'worlds of philip k dick, the', 'The-Worlds-Of-Philip-K-Dick', 'Yann', 'Coquart', '2016-03-02 00:00:00+00', 'https://philipdick.com/images/covers/TheWorldsOfPhilipKDick.jpg', 8, 'We draw upon Philip K. Dick''s work as well as various cinematographic adaptations of his novels in order to illustrate the extent to which K. Dick''s oeuvre foretold the world that has become our own today. We will take the viewer on a fascinating journey to discover this extraordinary writer.', NULL, true, '2020-10-31 16:54:41.99+00', '2020-10-31 16:54:41.99+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (203, 'The Electric Dreamer: Remembering Philip K. Dick', 'electric dreamer: remembering philip k. dick, the', 'The-Electric-Dreamer-Remembering-Philip-K-Dick', 'Charles', 'de Lauzirika', '2007-12-18 00:00:00+00', 'https://philipdick.com/images/covers/TheElectricDreamerRememberingPhilipKDick.jpg', 8, 'Friends, family and colleagues of Philip K. Dick remember the life and work of the author of ''Do Androids Dream of Electric Sheep?''', NULL, true, '2020-10-31 17:04:32.432+00', '2020-10-31 17:04:32.432+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (204, 'Philip K. Dick: A Day in the Afterlife', 'philip k. dick: a day in the afterlife', 'Philip-K-Dick-A-Day-In-The-Afterlife', 'Nicola', 'Roberts', '1994-01-29 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickADayInTheAfterlife.jpg', 8, 'A documentary which goes on an imaginative tour from the Colorado grave where Dick is buried to the suburbs of California where he lived and worked. Talks to his ex-wives, friends and biographers.', NULL, true, '2020-10-31 17:07:37.563+00', '2020-10-31 17:08:48.276+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (193, 'Precious Artifacts 3 - Précieuses Reliques: Une Bibliographie De Philip K. Dick - Éditions Françaises', 'precious artifacts 3 - précieuses reliques: une bibliographie de philip k. dick - éditions françaises', 'Precious-Artifacts-3-Précieuses-Reliques-Une-Bibliographie-De-Philip-K-Dick-Éditions-Françaises', 'Henri Wintz,', 'David Hyde', '2019-02-20 00:00:00+00', 'https://philipdick.com/images/covers/PreciousArtifacts3PrcieusesReliquesUneBibliographieDePhilipKDickditionsFranaises.jpg', 15, 'Dans ce volume, le troisième de la série PRECIOUS ARTIFACTS, David Hyde et Henri Wintz poursuivent leur odyssée à travers la bibliographie de Philip K. Dick en abordant les traductions françaises de ses écrits. Cet ouvrage est le premier compte rendu exhaustif, et entièrement illustré, de l’histoire éditoriale de l’oeuvre de Philip K. Dick en France. Avec plus de 400 livres et magazines référencés, de nombreuses informations sur les éditeurs, les traducteurs, et les illustrateurs de son oeuvre, cet ouvrage est essentiel pour les les fans et les collectionneurs des livres de Philip K. Dick. Presque entièrement bilingue, le livre plaira aux amateurs de PKD du monde entier.In this volume, the third in the PRECIOUS ARTIFACTS series, David Hyde and Henri Wintz continue their odyssey through Philip K. Dick’s bibliography by tackling the French translations of his writings. This work is the first comprehensive, fully illustrated, review of Philip K. Dick’s publication history in France. With more than 400 books and magazines referenced, plenty of information on the publishers, translators and illustrators of his books, it is a must-have for Philip K. Dick fans and book collectors. Almost entirely bilingual, the book will please PKD enthusiasts all over the world.', NULL, true, '2020-10-30 20:17:51.131+00', '2020-10-30 20:17:51.131+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (194, 'PKD: A Phillip K. Dick Bibliography', 'pkd: a phillip k. dick bibliography', 'PKD-A-Phillip-K-Dick-Bibliography', 'Daniel J.H.', 'Levack', '1988-08-01 00:00:00+00', 'https://philipdick.com/images/covers/PKDAPhillipKDickBibliography.jpg', 15, 'This descriptive bibliography of the works of science fiction author, Phillip K. Dick, covers all the editions and first periodical appearances of his work up until 1981. Annotations are by Steven Owen Godersky and illustrations are included.', NULL, true, '2020-10-30 20:23:35.889+00', '2020-11-04 00:39:15.814+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (195, 'Philip Kindred Dick, Metaphysical Conjurer: A Working Bibliography', 'philip kindred dick, metaphysical conjurer: a working bibliography', 'Philip-Kindred-Dick-Metaphysical-Conjurer-A-Working-Bibliography', 'Phil Stephensen-Payne,', 'Gordon Benson', '1992-01-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKindredDickMetaphysicalConjurerAWorkingBibliography.jpg', 15, NULL, NULL, true, '2020-10-30 20:32:27.164+00', '2020-10-30 20:32:27.164+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (196, 'Prophets of Science Fiction', 'prophets of science fiction', 'Prophets-Of-Science-Fiction', 'Ridley', 'Scott', '2011-11-16 00:00:00+00', 'https://philipdick.com/images/covers/ProphetsOfScienceFiction.jpg', 8, 'What once was just imagination is now real; what was once the distant future is now around the corner. The ''Science Fiction'' of the past has now simply become ''Science''. And the science of the future was strangely prophesied by a group of visionaries whose dreams once may have deemed them renegades and ''mad scientists,'' have become reality! Each episode will examine, dissect, measure, scrutinise and celebrate the life; ideas, convictions, philosophies and genius of each legend from the science fiction realm, each of these greats are pillars of the genre, representing the most ground-breaking conceptual viewpoints of their era. Notable for some of today''s greatest sci-fi concepts these scholars will forever be remembered in the minds of young and old!', NULL, true, '2020-10-31 15:41:33.717+00', '2020-10-31 15:41:33.717+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (197, 'The Penultimate Truth About Philip K. Dick', 'penultimate truth about philip k. dick, the', 'The-Penultimate-Truth-About-Philip-K-Dick', 'Emiliano', 'Larre', '2007-01-01 00:00:00+00', 'https://philipdick.com/images/covers/ThePenultimateTruthAboutPhilipKDick.jpg', 8, 'Philip K. Dick is considered by many to be one the world''s greatest science fiction writers ever; as a sufferer from mental illness himself he had the ability to turn his hallucinations about the universe into an extraordinary writing career.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (198, 'The Gospel According to Philip K. Dick', 'gospel according to philip k. dick, the', 'The-Gospel-According-To-Philip-K-Dick', 'Mark', 'Steensland', '2010-07-13 00:00:00+00', 'https://philipdick.com/images/covers/TheGospelAccordingToPhilipKDick.jpg', 8, 'Philip K. Dick may be science fiction''s greatest writer ever. His writing and ideas on reality, humanity and technology blend West Coast Utopianism, counter-culture paranoia and mystical experience. His work has been adapted into films including ''Blade Runner'', ''Total Recall'', ''Imposter'', and Steven Spielberg''s ''Minority Report'', starring Tom Cruise. His novels and stories continue to inspire and influence a generation of filmmakers, writers, technophiles and philosophers. But for the last ten years of his life, he inhabited a reality stranger than the fiction he created.', NULL, true, '2020-10-31 16:17:36.876+00', '2020-10-31 16:17:36.876+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (199, 'Blade Runner', 'blade runner', 'Blade-Runner-Video-Game', NULL, 'Westwood Studios', '1997-11-03 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerVideoGame.jpg', 11, 'In 1997 Westwood Studios released a title that won universal praise from gamers and critics all around the world, along with accolades like AIAS’s Adventure Game of the Year award. Set in the same time and universe as the 1982 classic science fiction film by Ridley Scott, Blade Runner video game is now making a big comeback on modern computers.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (208, 'The Man in the High Castle', 'man in the high castle, the', 'The-Man-In-The-High-Castle-Television', 'Frank', 'Spotnitz', '2015-11-20 00:00:00+00', 'https://philipdick.com/images/covers/TheManInTheHighCastleTelevision.jpg', 7, 'Based on Philip K. Dick''s award-winning novel, and executive produced by Ridley Scott (Blade Runner), and Frank Spotnitz (The X-Files), The Man in the High Castle explores what it would be like if the Allied Powers had lost WWII, and Japan and Germany ruled the United States. Starring Rufus Sewell (John Adams), Luke Kleintank (Pretty Little Liars), and Alexa Davalos (Mob City).', NULL, true, '2020-11-03 04:38:16.679+00', '2020-11-04 00:20:32.178+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (209, 'The Man in the High Castle and Philosophy: Subversive Reports from Another Reality', 'man in the high castle and philosophy: subversive reports from another reality, the', 'The-Man-In-The-High-Castle-And-Philosophy-Subversive-Reports-From-Another-Reality', 'Bruce Krajewski,', 'Joshua Heter', '2017-07-10 00:00:00+00', 'https://philipdick.com/images/covers/TheManInTheHighCastleAndPhilosophy.jpg', 4, 'The Man in the High Castle is an Amazon TV show, based on the Philip K. Dick novel, about an "alternate present" (beginning in the 1960s) in which Germany and Japan won World War II, with the former Western US occupied by Japan, the former Eastern U.S. occupied by Nazi Germany, and a small "neutral zone" between them. A theme of the story is that in this alternative world there is eager speculation, fueled by the illicit newsreel, The Grasshopper Lies Heavy, about how the world would have been different if America had won the war.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (210, 'Philip K. Dick''s Electric Dreams', 'philip k. dick''s electric dreams', 'Philip-K-Dick-S-Electric-Dreams-Television', NULL, NULL, '2018-01-12 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDicksElectricDreamsTelevision.jpg', 7, 'Based on the short stories from one of science fiction’s most prolific authors, Philip K. Dick''s Electric Dreams explores humanity in 10 standalone episodes. From 5 to 5000 years in the future, each story in the anthology will question what it means to be human in uniquely ambitious, grounded, yet fantastical worlds.', NULL, true, '2020-11-03 15:16:48.435+00', '2020-11-04 00:20:23.607+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (211, 'Blade Runner 2019', 'blade runner 2019', 'Blade-Runner-2019', 'Michael Green, Mike Johnson,', 'Andres Guinaldo', '2019-11-19 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2019.jpg', 17, 'Winner of the 2020 SCRIBE AWARD for best Graphic Novel, this critically acclaimed graphic novel, is the official sequel to the cult classic 1982 science fiction film directed by Ridley Scott. Co-written by Michael Green, the Academy Award nominated screenwriter of Logan, Blade Runner 2049, and Murder on the Orient Express.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (213, 'Blade Runner 2049', 'blade runner 2049', 'Blade-Runner-2049', 'Denis', 'Villeneuve', '2017-10-06 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunner2049.jpg', 6, 'A new blade runner unearths a secret that could plunge what’s left of society into chaos and leads him on a quest to find a former LAPD blade runner who''s been missing for 30 years.', NULL, true, '2020-11-03 15:43:47.801+00', '2020-11-03 15:43:47.801+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (214, 'Total Recall', 'total recall', 'Total-Recall-Film-1990', 'Paul', 'Verhoeven', '1990-06-01 00:00:00+00', 'https://philipdick.com/images/covers/TotalRecallFilm.jpg', 6, 'Action star extraordinaire Arnold Schwarzenegger is Quaid, a 2084 construction worker haunted by dreams of Mars. Against the wishes of his wife (Sharon Stone), Quaid goes to a company that implants artificial memories, so he can "remember" visiting the red planet. However, Quaid is actually a secret agent from Mars - or is he?', NULL, true, '2020-11-03 15:58:25.475+00', '2020-11-04 00:19:25.252+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (215, 'Barjo', 'barjo', 'Barjo', 'Jérôme', 'Boivin', '1992-05-13 00:00:00+00', 'https://philipdick.com/images/covers/Barjo.jpg', 6, 'The narrator, "Barjo" (nutcase, crap artist), is an obsessive simpleton, given to filling his notebook with verbatim dialog, observed trivia, and oddball speculation on human behavior and the end of the world. When his house burns, he moves in with his twin sister, Fanfan -- an impulsive, quixotic egoist -- and her husband, Charles, the Aluminum King. Charles becomes the focus of the film, as his wife and brother-in-law bewilder him.', NULL, true, '2020-11-03 16:03:46.042+00', '2020-11-03 16:06:26.209+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (216, 'Screamers', 'screamers', 'Screamers', 'Christian', 'Duguay', '1996-01-26 00:00:00+00', 'https://philipdick.com/images/covers/Screamers.jpg', 6, 'A rebel commander (Peter Weller, Robocop, 1987) must protect his outpost from the programmed weapons that are mutating into killers of all human life.', NULL, true, '2020-11-03 16:09:35.205+00', '2020-11-03 16:09:35.205+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (217, 'Screamers: The Hunting', 'screamers: the hunting', 'Screamers-The-Hunting', 'Sheldon', 'Wilson', '2009-02-17 00:00:00+00', 'https://philipdick.com/images/covers/ScreamersTheHunting.jpg', 6, 'In this sequel to the cult classic SCREAMERS, a rescue team is lured by a distress signal to a long abandoned planet where they encounter an army of robotic killing machines whose sole objective is to escape to Earth and destroy the human race.', NULL, true, '2020-11-03 16:14:04.007+00', '2020-11-03 16:14:04.007+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (218, 'Impostor', 'impostor', 'Impostor', 'Gary', 'Fleder', '2002-01-04 00:00:00+00', 'https://philipdick.com/images/covers/Impostor.jpg', 6, 'An engineer creates the ultimate weapon in a battle against aliens, only to be suspected as an alien himself. Second in a three-part anthology on relationships between humans and aliens.', NULL, true, '2020-11-03 16:19:40.043+00', '2020-11-03 16:19:40.043+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (219, 'Minority Report', 'minority report', 'Minority-Report-Film', 'Steven', 'Spielberg', '2002-06-21 00:00:00+00', 'https://philipdick.com/images/covers/MinorityReportFilm.jpg', 6, 'In the future, a division of the police department has the technology to arrest killers before they commit crimes; however, the tables turn on one of the cops working within the division.', NULL, true, '2020-11-03 16:23:28.017+00', '2020-11-04 00:18:27.941+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (220, 'Paycheck', 'paycheck', 'Paycheck', 'John', 'Woo', '2003-12-25 00:00:00+00', 'https://philipdick.com/images/covers/Paycheck.jpg', 6, 'Michael Jennings works on top-secret projects. After each job, his memory is erased. After his latest assignment he is shocked to be told that he agreed to forfeit all payment, until he receives an envelope containing clues to his forgotten past.', NULL, true, '2020-11-03 16:29:51.954+00', '2020-11-03 16:29:51.954+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (212, 'Blade Runner', 'blade runner', 'Blade-Runner-Film', 'Ridley', 'Scott', '1982-06-25 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerFilm.jpg', 6, 'Rick Deckard prowls the steel-and-microchip jungle of 21st Century Los Angeles, stalking genetically made criminal replicants. His assignment: kill them.', NULL, true, '2020-11-03 15:35:39.754+00', '2020-11-09 21:34:37.734+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (222, 'Next', 'next', 'Next', 'Lee', 'Tamahori', '2007-04-27 00:00:00+00', 'https://philipdick.com/images/covers/Next.jpg', 6, 'Las Vegas showroom magician Cris Johnson has a secret: he can see two minutes into the future. Sick of the government and scientific interest in his gift, he lies low in Vegas, performing cheap tricks and living off small-time gambling "winnings." But when a terrorist group threatens to detonate a nuclear device in Los Angeles, government agent Callie Ferris must use all her wiles to capture Cris and convince him to help her stop the cataclysm.', NULL, true, '2020-11-03 16:40:41.049+00', '2020-11-03 16:40:41.049+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (223, 'The Adjustment Bureau', 'adjustment bureau, the', 'The-Adjustment-Bureau', 'George', 'Nolfi', '2011-03-04 00:00:00+00', 'https://philipdick.com/images/covers/TheAdjustmentBureau.jpg', 6, 'Fate takes a hand in the pre-planned lives of a politician and a ballerina in this intriguing tale starring Matt Damon and Emily Blunt.', NULL, true, '2020-11-03 16:46:52.771+00', '2020-11-03 16:46:52.771+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (224, 'Total Recall', 'total recall', 'Total-Recall-Film-2012', 'Len', 'Wiseman', '2012-08-03 00:00:00+00', 'https://philipdick.com/images/covers/TotalRecallFilm2012.jpg', 6, 'Total Recall is an action thriller about reality and memory, inspired anew by the famous short story “We Can Remember It For You Wholesale” by Philip K. Dick.', NULL, true, '2020-11-03 16:51:10.258+00', '2020-11-04 00:19:41.86+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (225, 'Radio Free Albemuth', 'radio free albemuth', 'Radio-Free-Albemuth-Film', 'John Alan', 'Simon', '2014-06-27 00:00:00+00', 'https://philipdick.com/images/covers/RadioFreeAlbemuthFilm.jpg', 6, 'Sci-fi thriller from Philip K. Dick (Blade Runner, Minority Report, A Scanner Darkly); Nick Brady receives messages from space and joins conspiracy to overthrow authoritarian U.S. regime', NULL, true, '2020-11-03 17:02:51.346+00', '2020-11-04 00:18:49.385+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (226, 'The American Book of the Dead', 'american book of the dead, the', 'The-American-Book-Of-The-Dead', 'Henry', 'Baum', '2011-11-22 00:00:00+00', 'https://philipdick.com/images/covers/TheAmericanBookOfTheDead.jpg', 19, 'Eugene Myers is working on a novel about the end of the world. Meanwhile, he discovers his daughter doing porn online and his marriage is coming to an end. When he begins dreaming about people who turn out to be real, he wonders if his novel is real as well. Eugene Myers may just be the one to stop the apocalypse. This history of the future covers every conspiracy imaginable: UFOs, secret societies, and World War III, as well as theories on life after death and human evolution by a writer whose last novel was called by Dogmatika, "A page-turner and an example of an effective piece of storytelling that should be envied." In the tradition of Philip K. Dick and Robert Anton Wilson, The American Book of the Dead explores the nature of reality and the human race''s potential to either disintegrate or evolve.', NULL, true, '2020-11-07 20:48:17.119+00', '2020-11-07 20:48:17.119+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (227, 'The Cardboard Universe: A Guide to the World of Phoebus K. Dank', 'cardboard universe: a guide to the world of phoebus k. dank, the', 'The-Cardboard-Universe-A-Guide-To-The-World-Of-Phoebus-K-Dank', 'Christopher', 'Miller', '2009-04-14 00:00:00+00', 'https://philipdick.com/images/covers/TheCardboardUniverseAGuideToTheWorldOfPhoebusKDank.jpg', 19, 'Genius or fraud? Hack or Hemingway? The life and work of obese, obsessive, logorrheic pulp novelist Phoebus K. Dank have long enflamed bitter controversy—and numerous drunken rants often culminating in vomiting, unconsciousness, or both. In this uproarious novel, Christopher Miller pulls back the curtain on two unforgettable critics—fawning scholar William Boswell (the world''s leading Dankian) and his mortal enemy, the murderously snarky Owen Hirt. No stone is left unturned—and no gooey mess unstepped in—in this essential study of Dank''s all-too-brief existence and all-too-extensive oeuvre.', NULL, true, '2020-11-07 21:02:09.003+00', '2020-11-07 21:02:09.003+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (246, 'Great Lives: Philip K. Dick', 'great lives: philip k. dick', 'Great-Lives-Philip-K-Dick', NULL, NULL, '2011-12-01 00:00:00+00', 'https://philipdick.com/images/covers/GreatLivesPhilipKDick.jpg', 9, 'The actor Michael Sheen nominates the sci-fi author Philip K Dick. With Matthew Parris and Roger Luckhurst.', NULL, true, '2020-11-10 02:19:42.045+00', '2020-11-10 02:19:42.045+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (248, 'Electric Dreams Podcast', 'electric dreams podcast', 'Electric-Dreams-Podcast', NULL, 'Golden Spiral Media', '2017-09-21 00:00:00+00', 'https://philipdick.com/images/covers/ElectricDreamsPodcast.jpg', 18, 'Join your hosts, Wayne Henderson and Steve Salyer, for in-depth theories, discussion, fun, and your thoughts about the TV series Electric Dreams. From the genius of the Philip K. Dick, comes a series of short stories adapted for the small screen by a creative team including Ronald D. Moore and Bryan Cranston! The Electric Dreams Podcast is an unofficial, fan podcast, on the Golden Spiral Media podcast network. Wayne also hosts the Doctor Who podcast, Don''t Blink, as well as the Packers Fan Podcast. Steve previously hosted the Frequency podcast Radio Static, and the Sleepy Hollow podcast, Witness Prophecies, also on Golden Spiral Media.', NULL, true, '2020-11-10 02:35:07.156+00', '2020-11-10 02:35:07.156+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (228, 'First Cause: A Novel About Human Possibility', 'first cause: a novel about human possibility', 'First-Cause-A-Novel-About-Human-Possibility', 'Paul', 'West', '2012-07-01 00:00:00+00', 'https://philipdick.com/images/covers/FirstCauseANovelAboutHumanPossibility.jpg', 19, 'The world is in chaos from a two-day battery of explosions in many of its major cities.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (252, 'Blade Runner Scene-by-Scene', 'blade runner scene-by-scene', 'Blade-Runner-Scene-by-Scene', 'John David', 'Ebert', '2015-12-30 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerScenebyScene.jpg', 5, 'With this new book by John David Ebert, Blade Runner Scene-by-Scene, Ebert performs an archaeology of Ridley Scott''s main thematic concerns: death, origin, genesis and the quest for immortality. In doing so, he compares the film to such ancient and classic texts as The Gilgamesh Epic. Scott''s vision of the Los Angeles of 2019 is seen as a type of decaying, late stage cosmopolis; and the Replicants as slave labor manufactured to work their modern latifundia.', NULL, true, '2020-12-24 16:46:13.438+00', '2020-12-24 16:46:13.438+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (229, 'A Kindred Spirit', 'kindred spirit, a', 'A-Kindred-Spirit', 'ej', 'Morgan', '2011-08-02 00:00:00+00', 'https://philipdick.com/images/covers/AKindredSpirit.jpg', 19, 'Philip K. Dick is dead but he’s not done. The semi-famous “sci-fi” writer, (aka the Prince of Pulp or fictionalizing philosopher) has Unfinished Business. To further complicate things, he is tangled up in the afterlife with the heretical former Episcopalian Bishop of California, James Pike. The two have finally figured out—well, everything, but need a way to convey their message to the masses.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (230, 'The Lathe Of Heaven', 'lathe of heaven, the', 'The-Lathe-Of-Heaven', 'Ursula K.', 'Le Guin', '1971-03-01 00:00:00+00', 'https://philipdick.com/images/covers/TheLatheOfHeaven.jpg', 19, 'A classic science fiction novel by Ursula K. Le Guin, one of the greatest writers of the genre, set in a future world where one man’s dreams control the fate of humanity.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (231, 'Pandemonium', 'pandemonium', 'Pandemonium', 'Daryl', 'Gregory', '2008-08-26 00:00:00+00', 'https://philipdick.com/images/covers/Pandemonium.jpg', 19, 'It is a world like our own in every respect . . . save one. In the 1950s, random acts of possession begin to occur. Ordinary men, women, and children are the targets of entities that seem to spring from the depths of the collective unconscious, pop-cultural avatars some call demons. There’s the Truth, implacable avenger of falsehood. The Captain, brave and self-sacrificing soldier. The Little Angel, whose kiss brings death, whether desired or not. And a string of others, ranging from the bizarre to the benign to the horrific.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (249, 'The Truman Show', 'truman show, the', 'The-Truman-Show', NULL, NULL, '1998-06-05 00:00:00+00', 'https://philipdick.com/images/covers/TheTrumanShow.jpg', 6, 'Truman doesn’t realize that his quaint hometown is a giant studio set run by a visionary producer/director/creator (Ed Harris), that folks living and working there are Hollywood actors, that even his incessantly bubbly wife is a contract player.', NULL, true, '2020-11-10 02:51:55.354+00', '2020-11-10 02:51:55.354+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (232, 'Philip K. Dick Is Dead, Alas', 'philip k. dick is dead, alas', 'Philip-K-Dick-Is-Dead-Alas', 'Michael', 'Bishop', '1987-11-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickIsDeadAlas.jpg', 19, 'In this heartfelt science-fiction homage, Philip K. Dick dies in 1982 in Santa Ana, California, during the fourth term of the repressive imperial presidency of Richard Milrose Nixon. Soon thereafter, stripped of his memory, Dick turns up in the office of Lia Bonner, a young psychotherapist in Warm Springs, Georgia. Ultimately, Dick manifests at Von Braunville, the American moon base, as a key figure in a gonzo conspiracy to trigger a "redemptive shift" of world-changing scope. Indeed, according to The New York Times, the ending of Michael Bishop''s wittily inventive Dickian extravaganza "approaches sublimity."', NULL, true, '2020-11-09 14:52:48.56+00', '2020-11-09 14:57:56.243+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (247, 'Now Playing: The Philip K. Dick Retrospective Series', 'now playing: the philip k. dick retrospective series', 'Now-Playing-The-Philip-K-Dick-Retrospective-Series', NULL, NULL, '2011-01-07 00:00:00+00', 'https://philipdick.com/images/covers/NowPlayingThePhilipKDickRetrospectiveSeries.jpg', 18, 'Hollywood''s biggest directors and actors have always been drawn to the dark and paranoid science fiction of author Philip K. Dick, from Ridley Scott''s Blade Runner to Arnold Schwarzenegger''s Total Recall to the upcoming Matt Damon film The Adjustment Bureau. Join Now Playing as Brock, Jakob, and Stuart delve into the mind of this imaginative writer and evaluate each big screen adaptation of his books and short stories.', NULL, true, '2020-11-10 02:30:30.039+00', '2020-11-10 02:30:30.039+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (113, 'Philip K. Dick: The Last Interview and Other Conversations', 'philip k. dick: the last interview and other conversations', 'Philip-K-Dick-The-Last-Interview-And-Other-Conversations', 'David', 'Streitfeld', '2015-12-15 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickTheLastInterviewAndOtherConversations.jpg', 16, 'An electric collection of interviews—including the first and the last—with one of the 20th century''s most prolific, influential, and dazzlingly original writers of science fiction
