INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (251, 'Blade Runner: Pocket Guide', 'blade runner: pocket guide', 'Blade-Runner-Pocket-Guide', 'Jeremy Mark', 'Robinson', '2016-03-28 00:00:00+00', 'https://philipdick.com/images/covers/BladeRunnerPocketGuide.jpg', 5, 'BLADE RUNNER: POCKET MOVIE GUIDE REVISED AND UPDATED. HARDCOVER.
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (233, 'Philip K. Dick High', 'philip k. dick high', 'Philip-K-Dick-High', 'David', 'Bischoff', '2000-06-01 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickHigh.jpg', 19, 'THE HIGH SCHOOL OF THE FUTURE . . . "You''ve never wondered what the meaning of life is, have you?" asked my teacher. I shook my head. "I thought people who were depressed asked those kind of questions." "Okay. Then Quinn, I''ll have to be more direct. Middlevale isn''t Middlevale. Eisenhower isn''t truly an accredited American high school. I''m not me, and you''re not you!" "Then who are we?" "Victims!" Her finger shot in the air. "Victims of some sort of experiment! Some kind of psycho-social experiment perpetrated by scientists without principle, a government without morals!" Her dewlaps quivered with indignity. My head was spinning again. I tried to speak but I couldn''t. "These ears . . . open them up and you''ll find microchip monitors and controls. And judging by the kind of ''visions'' we''ve both seen, I''d also say you''d also find some kind of mind-cloak device, adjusted to auditory and visual aspects of our brains, normalizing the odd things that may abound in this laboratory environment." I blinked. "You mean, it''s all a joke?" "A bad one. A total farce." "You''re telling me . . . You''re saying that it''s all a set-up? But how long has this been going on, then?" "Hard to say. Part of your memories could have been programmed." "Programmed?" I stared. "Like computers."', NULL, true, '2020-11-09 15:14:14.625+00', '2020-11-09 15:14:14.625+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (234, 'The Word of God: Or, Holy Writ Rewritten', 'word of god: or, holy writ rewritten, the', 'The-Word-Of-God-Or-Holy-Writ-Rewritten', 'Thomas M.', 'Disch', '2008-07-01 00:00:00+00', 'https://philipdick.com/images/covers/TheWordOfGodOrHolyWritRewritten.jpg', 19, 'Not since The Da Vinci Code!
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (244, 'American Writers (One Hundred Pages at a Time)', 'american writers (one hundred pages at a time)', 'American-Writers-One-Hundred-Pages-At-A-Time', 'Evan', 'Lampe', '2017-02-19 00:00:00+00', 'https://philipdick.com/images/covers/AmericanWritersOneHundredPagesAtATime.jpg', 18, 'In each episode I discuss around 100 pages from the works of American writers.', NULL, true, '2020-11-10 02:07:09.234+00', '2020-11-10 02:07:09.234+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (236, 'Faction Paradox: Of the City of the Saved...', 'faction paradox: of the city of the saved...', 'Faction-Paradox-Of-The-City-Of-The-Saved', 'Philip', 'Purser-Hallard', '2004-04-15 00:00:00+00', 'https://philipdick.com/images/covers/FactionParadoxOfTheCityOfTheSaved.jpg', 19, 'Laura Tobin''s a private investigator who''s summoned to investigate a very peculiar murder --- one that occurs in The City of the Saved-a haven at the end of the Universe, populated by every human being or pseudo-human being who''s ever lived. Except that in the City, all murders are literally impossible. But Laura''s got a very dead body to prove otherwise. As part of her investigation, Laura will come across the machinations of the various powers within the City, including the Rump Parliament and the City Council and more --- and also perhaps the Secret Archiects who built the City in the first place. And then there''s Faction Paradox, a group of time-travelling ritualists, saboteurs and subterfugers -- essentially, the criminal-cult to end all criminal-cults. As always, the Faction''s trying to subvert history to its own ends, preferably by letting its rivals kill each other off, then swooping in to seize whatever''s left -- presuming the Universe survives the conflict...', NULL, true, '2020-11-09 15:30:22.173+00', '2020-11-09 15:31:17.053+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (235, 'Kindred Blood in Kensington Gore', 'kindred blood in kensington gore', 'Kindred-Blood-In-Kensington-Gore', 'Brian W.', 'Aldiss', '1992-01-01 00:00:00+00', 'https://philipdick.com/images/covers/KindredBloodInKensingtonGore.jpg', 13, 'Philip K. Dick in the Afterlife : an Imaginary Conversation', NULL, true, '2020-11-09 15:25:20.522+00', '2020-11-09 15:55:41.661+00');
