INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (238, 'The Two Georges', 'two georges, the', 'The-Two-Georges', 'Stephen', 'Keyworth', '2014-06-01 00:00:00+00', 'https://philipdick.com/images/covers/TheTwoGeorges.jpg', 9, 'Comedy drama that imagines what might have followed after writer Philip K Dick was investigated by the FBI.', NULL, true, '2020-11-09 15:55:17.347+00', '2020-11-09 15:55:17.347+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (237, '800 Words: The Transmigration of Philip K. Dick', '800 words: the transmigration of philip k. dick', '800-Words-The-Transmigration-Of-Philip-K-Dick', 'Victoria', 'Stewart', '2009-01-01 00:00:00+00', 'https://philipdick.com/images/covers/800WordsTheTransmigrationOfPhilipKDick.jpg', 13, 'This is not the story of a madman.  Many of the myths surrounding the man who brought us Blade Runner bring to mind a reclusive hermit, with wild hair and wilder eyes, churning out drug-fueled fictions while slowly killing himself on a cocktail of amphetamines and alcohol.  
