INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (242, 'DickHeads Podcast', 'dickheads podcast', 'DickHeads-Podcast', NULL, NULL, '2018-04-15 00:00:00+00', 'https://philipdick.com/images/covers/DickHeadsPodcast.jpg', 18, 'Like a pink laser beam of nonsense beaming straight into your brain hole longtime Dickheads, authors David Agranoff and Anthony Trevino, break down each of Philip K. Dick''s novels in order of publication with very little help from their engineer Langhorne J Tweed. Plus: Interviews. Story Vs. Film episodes. And other bits of PKD history.', NULL, true, '2020-11-10 01:43:05.61+00', '2020-11-10 01:43:05.61+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (243, 'We Love Dick: A Philip K. Dick Podcast', 'we love dick: a philip k. dick podcast', 'We-Love-Dick-A-Philip-K-Dick-Podcast', NULL, NULL, '2015-10-08 00:00:00+00', 'https://philipdick.com/images/covers/WeLoveDickAPhilipKDickPodcast.png', 18, 'Join Wa and Foot on their quest to read through all 124 Philip K. Dick short stories. Tangents, foul language, and overly personal anecdotes are expected.', NULL, true, '2020-11-10 01:53:12.575+00', '2020-11-10 01:55:13.999+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (245, 'Philip K. Dick Philosophical Podcast', 'philip k. dick philosophical podcast', 'Philip-K-Dick-Philosophical-Podcast', NULL, NULL, '2011-10-28 00:00:00+00', 'https://philipdick.com/images/covers/PhilipKDickPhilosophicalPodcast.jpg', 18, 'Exploring the paranoid, hallucinatory future worlds of author Philip K. Dick. At the PKD Philosophical Podcast, we attempt to answer the important philosophical questions, like ''how tasty are martian go-birds?'', and ''why can''t androids dream of regular sheep like the rest of us?'' Each episode covers one short story, or part of a novel, starting with Philip K. Dick''s early short stories from 1952.', NULL, true, '2020-11-10 02:14:50.166+00', '2020-11-10 02:14:50.166+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (253, 'The Blot', 'blot, the', 'The-Blot', 'Jonathan Lethem', 'Laurence A. Rickels', '2016-01-01 00:00:00+00', 'https://philipdick.com/images/covers/TheBlot.jpg', 4, 'In this supplement to Jonathan Lethem’s novel A Gambler’s Anatomy, the renowned novelist engages in a concerted transatlantic dialogue with cult theorist Laurence A. Rickels, exploring the vicissitudes of popular culture and the profound influence of Philip K. Dick on their respective lines of flight. Foregrounding the introjections between California and Germany, they address a range of ideas, subjects and figures, from B-movies, science fiction, Wile E. Coyote and the Devil to trauma theory, Freud, Hitchcock and German Expressionism. Animating their zone of interrogation is the “blot”—an algorithm of innuendo, an uncanny defamiliarization of reality and “truth” wherein the trajectories of meaning, desire and perception fold into themselves like an origami in flames.', NULL, true, '2020-12-24 17:14:47.544+00', '2020-12-24 17:20:10.207+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (254, 'The X in Exegesis, Preview Edition: Philip K. Dick''s Religious Speculations', 'x in exegesis, preview edition: philip k. dick''s religious speculations, the', 'The-X-In-Exegesis-Preview-Edition-Philip-K-Dick-s-Religious-Speculations', 'Tessa B.', 'Dick', '2019-10-22 00:00:00+00', 'https://philipdick.com/images/covers/TheXInExegesisPreviewEditionPhilipKDicksReligiousSpeculations.jpg', 4, 'Philip K. Dick, whose stories inspired Blade Runner, Minority Report, Total Recall and many other films, experienced visions in 1974 as a sort of rescue from harassment that he had suffered for several years. His house was invaded, he was run out of town, he fled the country and he tried to kill himself. His visions of 1974 provided him with healing messages that led him on a journey of research and speculation into religion, philosophy and science that consumed the final 8 years of his life. Experts might question the nature of his visions, but the harassment that led up to them was real.', NULL, true, '2020-12-24 17:24:23.682+00', '2020-12-24 17:24:23.682+00');
INSERT INTO titles (`titleID`, `titleName`, `titleSort`, `titleURL`, `authorFirstName`, `authorLastName`, `publicationDate`, `imageName`, `categoryID`, `shortDescription`, `urlPKDweb`, active, `createdAt`, `updatedAt`) VALUES (255, 'More on the Exegesis of Philip K. Dick: A Work in Progress', 'more on the exegesis of philip k. dick: a work in progress', 'More-On-The-Exegesis-Of-Philip-K-Dick-A-Work-In-Progress', 'Tessa B.', 'Dick', '2020-12-15 00:00:00+00', 'https://philipdick.com/images/covers/MoreOnTheExegesisOfPhilipKDickAWorkInProgress.jpg', 4, 'Exploration of the Exegesis, with emphasis on Gnosticism and Dualism. How Philip K. Dick''s 1974 visions arose from a lifetime of experiences of and speculation about paranormal and religious matters. This work in progress will cover some of Dick''s novels, many of his speculations, and his experience of organized harassment in the early 1970s.', NULL, true, '2020-12-24 17:34:30.039+00', '2020-12-24 17:34:30.039+00');
